import { useState, useEffect, useCallback, useRef } from 'react'
import Desktop from './components/Desktop'
import Taskbar from './components/Taskbar'
import Window from './components/Window'
import StartMenu from './components/StartMenu'
import BiosScreen from './components/BiosScreen'
import FileExplorer from './components/FileExplorer'
import LoginScreen from './components/LoginScreen'
import ErrorDialog from './components/ErrorDialog'
import Terminal from './components/Terminal'
import ProjectViewer from './components/ProjectViewer'
import Minesweeper from './components/Minesweeper'
import BSOD from './components/BSOD'

import { useSounds } from './hooks/useSounds'
import { useWindowManager } from './hooks/useWindowManager'
import { WINDOW_CONFIGS } from './config/windows'
import { ICONS } from './config/icons'

// System states
const SYSTEM_STATE = {
    WAITING: 'waiting', // Waiting for user to focus window
    BOOTING: 'booting', // BIOS boot sequence
    LOGGING: 'logging', // Login screen with startup sound
    RUNNING: 'running', // Desktop is active
    SHUTDOWN: 'shutdown' // Shutdown sequence
}

// Mobile detection component
const MobileMessage = () => (
    <div className="mobile-message">
        <div className="mobile-message-content">
            <div className="mobile-icon">üñ•Ô∏è</div>
            <h1>Desktop Only</h1>
            <p>Please open this site on Desktop for the full experience.</p>
        </div>
    </div>
)

function App() {
    const { playError } = useSounds()
    const {
        windowStates,
        openWindows,
        activeWindowId,
        openWindow,
        closeWindow,
        minimizeWindow,
        toggleMaximize,
        bringToFront,
        handleDragStart,
        handleResizeStart,
        handleContentResize
    } = useWindowManager()

    const [runErrorDialog, setRunErrorDialog] = useState(null)
    const [startMenuOpen, setStartMenuOpen] = useState(false)
    const [runInput, setRunInput] = useState('')
    const [showBSOD, setShowBSOD] = useState(false)

    // Mobile detection
    const [isMobile, setIsMobile] = useState(() => {
        if (typeof window === 'undefined') return false
        return window.innerWidth <= 768 || ('ontouchstart' in window && window.innerWidth <= 1024)
    })

    // Listen for resize events to update mobile detection
    useEffect(() => {
        const checkMobile = () => {
            setIsMobile(
                window.innerWidth <= 768 || ('ontouchstart' in window && window.innerWidth <= 1024)
            )
        }
        window.addEventListener('resize', checkMobile)
        return () => window.removeEventListener('resize', checkMobile)
    }, [])

    // System boot state
    const [systemState, setSystemState] = useState(() => {
        // Check if we've already booted this session
        const hasBooted = sessionStorage.getItem('borgesOS-booted')
        return hasBooted ? SYSTEM_STATE.RUNNING : SYSTEM_STATE.WAITING
    })

    // Handle window focus to start boot sequence
    useEffect(() => {
        if (systemState !== SYSTEM_STATE.WAITING) return

        const handleFocus = () => {
            setSystemState(SYSTEM_STATE.BOOTING)
        }

        // If window is already focused, start booting
        if (document.hasFocus()) {
            setSystemState(SYSTEM_STATE.BOOTING)
        } else {
            window.addEventListener('focus', handleFocus)
        }

        return () => window.removeEventListener('focus', handleFocus)
    }, [systemState])

    // Handle boot completion
    const handleBootComplete = useCallback(() => {
        sessionStorage.setItem('borgesOS-booted', 'true')
        setSystemState(SYSTEM_STATE.RUNNING)
    }, [])

    // Handle shutdown completion
    const handleShutdownComplete = useCallback(() => {
        // Clear session and reload
        sessionStorage.removeItem('borgesOS-booted')
        sessionStorage.removeItem('borgesOS-resume-opened')
        localStorage.removeItem('win95-recent-programs')
        window.location.reload()
    }, [])

    // Auto-open Resume after boot completes
    const hasOpenedResumeRef = useRef(false)
    useEffect(() => {
        if (systemState === SYSTEM_STATE.RUNNING && !hasOpenedResumeRef.current) {
            // Check if this is a fresh boot (not page refresh during same session)
            const isFirstBoot = !sessionStorage.getItem('borgesOS-resume-opened')
            if (isFirstBoot) {
                // Small delay for smooth transition from BIOS to desktop
                const timer = setTimeout(() => {
                    openWindow('resume')
                    sessionStorage.setItem('borgesOS-resume-opened', 'true')
                }, 300)
                hasOpenedResumeRef.current = true
                return () => clearTimeout(timer)
            }
        }
    }, [systemState, openWindow])

    // Taskbar click handler
    const handleTaskbarClick = useCallback(
        (winId) => {
            const state = windowStates[winId]
            if (!state.isOpen || state.isMinimized) {
                openWindow(winId)
            } else if (activeWindowId === winId) {
                minimizeWindow(winId)
            } else {
                bringToFront(winId)
            }
        },
        [windowStates, activeWindowId, openWindow, minimizeWindow, bringToFront]
    )

    // Start menu handlers
    const toggleStartMenu = () => setStartMenuOpen((prev) => !prev)
    const closeStartMenu = () => setStartMenuOpen(false)

    // Run dialog handlers
    const handleRunSubmit = (e) => {
        e.preventDefault()
        const cmd = runInput.trim().toLowerCase()
        if (!cmd) return

        const aliases = ['about', 'projects', 'contact', 'mycomputer', 'resume', 'run', 'settings']
        if (aliases.includes(cmd)) {
            openWindow(cmd)
            closeWindow('run')
            setRunInput('')
        } else if (cmd.startsWith('http://') || cmd.startsWith('https://')) {
            window.open(cmd, '_blank')
            closeWindow('run')
            setRunInput('')
        } else {
            playError()
            setRunErrorDialog({
                title: 'Run',
                message: `Cannot find file '${cmd}' (or one of its components). Make sure the path and filename are correct and that all required libraries are available.`,
                type: 'error'
            })
        }
    }

    // Start menu item click
    const handleStartItemClick = (action, openId) => {
        if (openId) openWindow(openId)
        if (action === 'run') openWindow('run')
        if (action === 'shutdown') {
            // Start shutdown sequence
            closeStartMenu()
            setSystemState(SYSTEM_STATE.SHUTDOWN)
            return
        }
        closeStartMenu()
    }

    // Close start menu on outside click
    useEffect(() => {
        const handleClick = () => closeStartMenu()
        document.addEventListener('click', handleClick)
        return () => document.removeEventListener('click', handleClick)
    }, [])

    // Keyboard shortcuts
    useEffect(() => {
        const handleKeyDown = (e) => {
            // Don't trigger shortcuts when typing in input fields
            if (e.target.tagName === 'INPUT' || e.target.tagName === 'TEXTAREA') {
                // Only allow Escape in input fields
                if (e.key === 'Escape') {
                    if (windowStates.run?.isOpen && !windowStates.run?.isMinimized) {
                        closeWindow('run')
                    }
                }
                return
            }

            // Ctrl + R: Open Run dialog
            if (e.ctrlKey && e.key.toLowerCase() === 'r') {
                e.preventDefault()
                openWindow('run')
                return
            }

            // Alt + F4: Close active window
            if (e.altKey && e.key === 'F4') {
                e.preventDefault()
                if (activeWindowId) {
                    closeWindow(activeWindowId)
                }
                return
            }

            // Escape: Close run window, start menu, or deselect active window
            if (e.key === 'Escape') {
                if (startMenuOpen) {
                    closeStartMenu()
                } else if (windowStates.run?.isOpen && !windowStates.run?.isMinimized) {
                    closeWindow('run')
                    // Also clear potentially stuck active state
                    if (activeWindowId === 'run') minimizeWindow('run')
                } else if (activeWindowId) {
                    minimizeWindow(activeWindowId)
                }
                return
            }

            // Ctrl + M: Minimize active window
            if (e.ctrlKey && e.key.toLowerCase() === 'm') {
                e.preventDefault()
                if (activeWindowId) {
                    minimizeWindow(activeWindowId)
                }
                return
            }

            // Ctrl + 1-6: Quick open windows
            if (e.ctrlKey && e.key >= '1' && e.key <= '6') {
                e.preventDefault()
                const windowMap = {
                    1: 'about',
                    2: 'projects',
                    3: 'contact',
                    4: 'mycomputer',
                    5: 'resume',
                    6: 'settings'
                }
                const windowId = windowMap[e.key]
                if (windowId) {
                    openWindow(windowId)
                }
                return
            }
        }

        document.addEventListener('keydown', handleKeyDown)
        return () => document.removeEventListener('keydown', handleKeyDown)
    }, [
        windowStates.run,
        activeWindowId,
        startMenuOpen,
        closeWindow,
        openWindow,
        minimizeWindow,
        closeStartMenu
    ])

    // Show mobile message on mobile devices
    if (isMobile) {
        return <MobileMessage />
    }

    // Show BIOS screen during boot or shutdown
    if (systemState === SYSTEM_STATE.WAITING) {
        return (
            <div className="bios-screen waiting-screen">
                <div className="bios-content">
                    <div className="bios-line">Click anywhere or focus window to start...</div>
                    <span className="bios-cursor visible">‚ñà</span>
                </div>
            </div>
        )
    }

    if (systemState === SYSTEM_STATE.BOOTING) {
        return <BiosScreen mode="boot" onComplete={() => setSystemState(SYSTEM_STATE.LOGGING)} />
    }

    if (systemState === SYSTEM_STATE.LOGGING) {
        return <LoginScreen onComplete={handleBootComplete} />
    }

    if (systemState === SYSTEM_STATE.SHUTDOWN) {
        return <BiosScreen mode="shutdown" onComplete={handleShutdownComplete} />
    }

    return (
        <>
            <Desktop
                icons={ICONS.filter((icon) => icon.showOnDesktop !== false)}
                onIconDoubleClick={openWindow}
                onContextMenuAction={(action) => {
                    if (action === 'properties') {
                        openWindow('settings')
                    }
                }}
            />

            {/* Regular windows */}
            {Object.entries(WINDOW_CONFIGS).map(([id, config]) => (
                <Window
                    key={id}
                    id={id}
                    title={config.title}
                    state={windowStates[id]}
                    isActive={activeWindowId === id}
                    onClose={() => closeWindow(id)}
                    onMinimize={() => minimizeWindow(id)}
                    onMaximize={() => toggleMaximize(id)}
                    onTitlebarMouseDown={(e) => handleDragStart(e, id)}
                    onTitlebarDoubleClick={() => toggleMaximize(id)}
                    onWindowMouseDown={() => {
                        if (windowStates[id].isOpen && !windowStates[id].isMinimized) {
                            bringToFront(id)
                        }
                    }}
                    onResizeStart={
                        config.resizable !== false
                            ? (e, direction) => handleResizeStart(e, id, direction)
                            : undefined
                    }
                    bodyStyle={config.bodyStyle}
                >
                    {id === 'fileexplorer' ? (
                        <FileExplorer onOpenWindow={openWindow} />
                    ) : id === 'terminal' ? (
                        <Terminal openWindow={openWindow} triggerBSOD={() => setShowBSOD(true)} />
                    ) : id === 'minesweeper' ? (
                        <Minesweeper onResize={(w, h) => handleContentResize(id, w, h)} />
                    ) : id.startsWith('project_') ? (
                        <ProjectViewer
                            projectId={id.replace('project_', '')}
                            isOpen={windowStates[id]?.isOpen}
                            onOpenWindow={openWindow}
                            onClose={() => closeWindow(id)}
                        />
                    ) : (
                        config.content
                    )}
                </Window>
            ))}

            {/* Run window */}
            <Window
                id="run"
                title="Run"
                state={windowStates.run}
                isActive={activeWindowId === 'run'}
                onClose={() => closeWindow('run')}
                onMinimize={() => minimizeWindow('run')}
                onMaximize={() => toggleMaximize('run')}
                onTitlebarMouseDown={(e) => handleDragStart(e, 'run')}
                onTitlebarDoubleClick={() => toggleMaximize('run')}
                onWindowMouseDown={() => {
                    if (windowStates.run?.isOpen && !windowStates.run?.isMinimized) {
                        bringToFront('run')
                    }
                }}
                onResizeStart={(e, direction) => handleResizeStart(e, 'run', direction)}
                className="run-window"
                bodyStyle={{ padding: '16px' }}
            >
                <form onSubmit={handleRunSubmit} className="run-form">
                    <div className="run-content">
                        <img src="icons/run.png" alt="Run" className="run-icon" />
                        <div className="run-info">
                            <p>
                                Type the name of a program, folder, or document, and Windows will
                                open it for you.
                            </p>
                            <label>
                                Open:
                                <input
                                    type="text"
                                    value={runInput}
                                    onChange={(e) => setRunInput(e.target.value)}
                                    autoFocus
                                    placeholder="Type 'about', 'projects'..."
                                />
                            </label>
                        </div>
                    </div>
                    <div className="run-buttons">
                        <button type="submit">OK</button>
                        <button type="button" onClick={() => closeWindow('run')}>
                            Cancel
                        </button>
                        <button type="button">Browse...</button>
                    </div>
                </form>
            </Window>

            {/* BSOD */}
            {showBSOD && <BSOD onRestart={handleShutdownComplete} />}

            {/* Error Dialog */}
            {runErrorDialog && (
                <ErrorDialog
                    title={runErrorDialog.title}
                    message={runErrorDialog.message}
                    type={runErrorDialog.type}
                    onClose={() => setRunErrorDialog(null)}
                />
            )}

            {/* Taskbar */}
            <Taskbar
                openWindows={openWindows}
                activeWindowId={activeWindowId}
                onTaskClick={handleTaskbarClick}
                onStartClick={(e) => {
                    e.stopPropagation()
                    toggleStartMenu()
                }}
            />

            {/* Start Menu */}
            <StartMenu isOpen={startMenuOpen} onItemClick={handleStartItemClick} />
        </>
    )
}

export default App

import { useEffect, useCallback } from 'react'

const BSOD = ({ onRecover }) => {
    const handleRecover = useCallback(() => {
        if (onRecover) onRecover()
    }, [onRecover])

    // Auto-recover after 5 seconds or on any key press
    useEffect(() => {
        const timer = setTimeout(handleRecover, 5000)

        const handleKeyPress = () => handleRecover()
        const handleClick = () => handleRecover()

        document.addEventListener('keydown', handleKeyPress)
        document.addEventListener('click', handleClick)

        return () => {
            clearTimeout(timer)
            document.removeEventListener('keydown', handleKeyPress)
            document.removeEventListener('click', handleClick)
        }
    }, [handleRecover])

    return (
        <div className="bsod-screen">
            <div className="bsod-content">
                <p className="bsod-title">Windows</p>
                <br />
                <p>A fatal exception 0E has occurred at 0028:C0011E36 in VXD VMM(01) +</p>
                <p>00010E36. The current application will be terminated.</p>
                <br />
                <p>* Press any key to terminate the current application.</p>
                <p>* Press CTRL+ALT+DEL again to restart your computer. You will</p>
                <p> lose any unsaved information in all applications.</p>
                <br />
                <br />
                <p className="bsod-centered">Press any key to continue _</p>
                <br />
                <br />
                <p className="bsod-easter-egg">
                    (Just kidding! This is an easter egg. Shane's portfolio is working fine! üòÑ)
                </p>
            </div>
        </div>
    )
}

export default BSOD

import { useState, useEffect, useCallback } from 'react'
import LetterGlitch from './LetterGlitch'

// System hardware info (shared with My Computer window)
export const SYSTEM_INFO = {
    cpu: 'Quantum Core @ 4.2 GHz',
    ram: '32768 MB',
    hdd: '2 TB SSD',
    gpu: 'HyperGraphics 9000'
}

// BIOS text lines to display progressively
const BOOT_SEQUENCE = [
    { text: 'BorgesOS BIOS v1.0', delay: 0 },
    { text: 'Copyright (C) 2026 Shane Borges', delay: 200 },
    { text: '', delay: 300 },
    { text: 'Initializing system...', delay: 400 },
    { text: 'Detecting hardware...', delay: 700 },
    { text: `  CPU: ${SYSTEM_INFO.cpu} ... OK`, delay: 900 },
    { text: `  RAM: ${SYSTEM_INFO.ram} ... OK`, delay: 1100 },
    { text: `  HDD: ${SYSTEM_INFO.hdd} ... OK`, delay: 1300 },
    { text: `  GPU: ${SYSTEM_INFO.gpu} ... OK`, delay: 1500 },
    { text: '', delay: 1600 },
    { text: 'Loading BorgesOS...', delay: 1800 },
    { text: '', delay: 2200 },
    { text: 'Press any key to continue...', delay: 2500 }
]

const SHUTDOWN_SEQUENCE = [
    { text: 'Shutting down BorgesOS...', delay: 0 },
    { text: '', delay: 200 },
    { text: 'Saving user settings...', delay: 400 },
    { text: 'Closing applications...', delay: 700 },
    { text: 'Unmounting drives...', delay: 1000 },
    { text: '', delay: 1200 },
    { text: 'It is now safe to restart your computer.', delay: 1400 },
    { text: '', delay: 1600 },
    { text: 'Press any key to restart...', delay: 1800 }
]

function BiosScreen({ mode, onComplete }) {
    const [visibleLines, setVisibleLines] = useState([])
    const [showCursor, setShowCursor] = useState(true)
    const [waitingForInput, setWaitingForInput] = useState(false)

    const sequence = mode === 'boot' ? BOOT_SEQUENCE : SHUTDOWN_SEQUENCE
    const lastMessageDelay = sequence[sequence.length - 1].delay

    useEffect(() => {
        // Show lines progressively
        const timers = sequence.map((line, index) => {
            return setTimeout(() => {
                setVisibleLines((prev) => [...prev, line.text])
            }, line.delay)
        })

        // After last message, wait for user input
        const waitTimer = setTimeout(() => {
            setWaitingForInput(true)
        }, lastMessageDelay + 200)

        // Cursor blink
        const cursorInterval = setInterval(() => {
            setShowCursor((prev) => !prev)
        }, 500)

        return () => {
            timers.forEach((t) => clearTimeout(t))
            clearTimeout(waitTimer)
            clearInterval(cursorInterval)
        }
    }, [mode, lastMessageDelay])

    // Listen for any key or click to continue
    useEffect(() => {
        if (!waitingForInput) return

        const handleInput = () => {
            if (onComplete) onComplete()
        }

        document.addEventListener('keydown', handleInput)
        document.addEventListener('click', handleInput)

        return () => {
            document.removeEventListener('keydown', handleInput)
            document.removeEventListener('click', handleInput)
        }
    }, [waitingForInput, onComplete])

    return (
        <div className="bios-screen">
            <LetterGlitch
                glitchColors={['#00ff00', '#00cc00', '#006600']}
                glitchSpeed={40}
                outerVignette={true}
                centerVignette={false}
                smooth={true}
                opacity={0.15}
            />
            <div className="bios-content">
                {visibleLines.map((line, index) => (
                    <div key={index} className="bios-line">
                        {line || '\u00A0'}
                    </div>
                ))}
                <span className={`bios-cursor ${showCursor ? 'visible' : ''}`}>‚ñà</span>
            </div>
        </div>
    )
}

export default BiosScreen

import { useState, useEffect } from 'react'

const TIPS = [
    "It looks like you're trying to hire a developer. Would you like help?",
    "Tip: Open the Resume to learn more about Shane's experience!",
    'Did you know? You can type commands in the Run dialog (Ctrl+R).',
    "Try the Terminal! Type 'help' for a list of commands.",
    'Pro tip: Use the Settings to customize your theme colors!',
    'Looking for projects? Check out the Projects window!',
    'Want to get in touch? Open Outlook Express or use Contact!',
    'Fun fact: This portfolio was built with React and Vite!',
    'Drag windows around! You can also resize them from the corners.',
    'Double-click a titlebar to maximize a window!'
]

const Clippy = ({ onAction, onDismiss }) => {
    const [currentTip, setCurrentTip] = useState(0)
    const [isVisible, setIsVisible] = useState(false)
    const [isAnimating, setIsAnimating] = useState(false)

    // Check if Clippy should be permanently hidden (for this session)
    useEffect(() => {
        const isHidden = sessionStorage.getItem('win95-clippy-hidden')
        if (isHidden) {
            return
        }

        // Show Clippy after a delay
        const showTimer = setTimeout(() => {
            setIsAnimating(true)
            setIsVisible(true)
        }, 5000)

        return () => clearTimeout(showTimer)
    }, [])

    // Rotate tips
    useEffect(() => {
        if (!isVisible) return

        const tipTimer = setInterval(() => {
            setCurrentTip((prev) => (prev + 1) % TIPS.length)
        }, 30000) // 30 seconds (increased from 8s)

        return () => clearInterval(tipTimer)
    }, [isVisible])

    const handleDismiss = () => {
        setIsAnimating(true)
        setTimeout(() => {
            setIsVisible(false)
            if (onDismiss) onDismiss()
        }, 300)
    }

    const handleHideForever = () => {
        sessionStorage.setItem('win95-clippy-hidden', 'true')
        handleDismiss()
    }

    const handleNextTip = () => {
        setCurrentTip((prev) => (prev + 1) % TIPS.length)
    }

    const handleOpenResume = () => {
        if (onAction) onAction('resume')
    }

    if (!isVisible) return null

    return (
        <div className={`clippy-container ${isAnimating ? 'clippy-animate' : ''}`}>
            <div className="clippy-bubble">
                <button className="clippy-close" onClick={handleDismiss} title="Dismiss">
                    √ó
                </button>
                <p className="clippy-text">{TIPS[currentTip]}</p>
                <div className="clippy-buttons">
                    <button onClick={handleNextTip} className="clippy-btn">
                        Next Tip
                    </button>
                    <button onClick={handleOpenResume} className="clippy-btn clippy-btn-primary">
                        Open Resume
                    </button>
                </div>
                <button className="clippy-hide-forever" onClick={handleHideForever}>
                    Don't show again
                </button>
            </div>
            <div className="clippy-character">
                <img src="icons/msagent-0.png" alt="Clippy" className="clippy-img" />
            </div>
        </div>
    )
}

export default Clippy

import { useState, useRef, useCallback, useEffect } from 'react'
import DesktopIcon from './DesktopIcon'
import ContextMenu from './ContextMenu'
import Tooltip from './Tooltip'
import ErrorDialog from './ErrorDialog'

// Icons that should be positioned in the top-right corner by default
const TOP_RIGHT_ICONS = ['settings', 'mycomputer']

// Grid cell size for snapping
const GRID_SIZE = 100
const GRID_ROWS = 6

// Default grid positions for icons
const getDefaultPositions = (icons) => {
    const positions = {}
    let leftIndex = 0
    let rightIndex = 0

    icons.forEach((icon) => {
        if (TOP_RIGHT_ICONS.includes(icon.id)) {
            positions[icon.id] = {
                x: window.innerWidth - 100,
                y: 20 + rightIndex * 90
            }
            rightIndex++
        } else {
            const col = Math.floor(leftIndex / GRID_ROWS)
            const row = leftIndex % GRID_ROWS
            positions[icon.id] = {
                x: 20 + col * GRID_SIZE,
                y: 20 + row * 90
            }
            leftIndex++
        }
    })

    return positions
}

// Snap to grid utility
const snapToGrid = (x, y) => ({
    x: Math.round(x / GRID_SIZE) * GRID_SIZE + 20,
    y: Math.round(y / 90) * 90 + 20
})

// Icon properties data - simplified
const getIconProperties = (icon) => ({
    name: icon.label,
    path: `C:\\Desktop\\${icon.label}`,
    owner: 'Shane Borges'
})

function Desktop({ icons, onIconDoubleClick, onContextMenuAction }) {
    // Icon positions with localStorage persistence
    const [iconPositions, setIconPositions] = useState(() => {
        const saved = localStorage.getItem('desktop-icon-positions')
        const currentIconIds = icons.map((i) => i.id)

        if (saved) {
            try {
                const parsed = JSON.parse(saved)
                // Check if saved positions match current icons
                const savedIds = Object.keys(parsed)
                const allMatch =
                    currentIconIds.every((id) => savedIds.includes(id)) &&
                    savedIds.length === currentIconIds.length

                if (allMatch) {
                    return parsed
                }
                // Icons changed - recalculate positions
                localStorage.removeItem('desktop-icon-positions')
            } catch {
                // Invalid JSON, recalculate
            }
        }
        return getDefaultPositions(icons)
    })

    // Selected icons
    const [selectedIcons, setSelectedIcons] = useState(new Set())

    // Save positions to localStorage when they change
    useEffect(() => {
        localStorage.setItem('desktop-icon-positions', JSON.stringify(iconPositions))
    }, [iconPositions])

    // Handle window resize - reposition right-aligned icons
    useEffect(() => {
        const handleResize = () => {
            setIconPositions((prev) => {
                const newPositions = { ...prev }
                let rightIndex = 0
                icons.forEach((icon) => {
                    if (TOP_RIGHT_ICONS.includes(icon.id)) {
                        newPositions[icon.id] = {
                            x: window.innerWidth - 100,
                            y: 20 + rightIndex * 90
                        }
                        rightIndex++
                    }
                })
                return newPositions
            })
        }

        window.addEventListener('resize', handleResize)
        // Also trigger on mount to ensure correct positioning
        handleResize()
        return () => window.removeEventListener('resize', handleResize)
    }, [icons])

    // Dragging state
    const [draggingIcon, setDraggingIcon] = useState(null)
    const dragStartPos = useRef({ x: 0, y: 0 })
    const dragStartIconPos = useRef({ x: 0, y: 0 })

    // Selection box state
    const [selectionBox, setSelectionBox] = useState(null)
    const desktopRef = useRef(null)
    const isSelectingRef = useRef(false)
    const startPosRef = useRef({ x: 0, y: 0 })

    // Context menu state
    const [contextMenu, setContextMenu] = useState(null)
    const [iconContextMenu, setIconContextMenu] = useState(null)

    // Properties dialog
    const [propertiesDialog, setPropertiesDialog] = useState(null)

    // Handle icon drag start
    const handleIconMouseDown = useCallback(
        (e, iconId) => {
            if (e.button !== 0) return // Only left click

            e.stopPropagation()

            // Select this icon if not already selected
            if (!selectedIcons.has(iconId)) {
                setSelectedIcons(new Set([iconId]))
            }

            const iconPos = iconPositions[iconId] || { x: 0, y: 0 }
            dragStartPos.current = { x: e.clientX, y: e.clientY }
            dragStartIconPos.current = { x: iconPos.x, y: iconPos.y }
            setDraggingIcon(iconId)
        },
        [iconPositions, selectedIcons]
    )

    // Handle icon right-click
    const handleIconContextMenu = useCallback((e, icon) => {
        e.preventDefault()
        e.stopPropagation()

        setSelectedIcons(new Set([icon.id]))
        setContextMenu(null)
        setIconContextMenu({
            x: e.clientX,
            y: e.clientY,
            icon
        })
    }, [])

    // Handle mouse move for icon dragging or selection
    const handleMouseMove = useCallback(
        (e) => {
            // Handle icon dragging
            if (draggingIcon) {
                const deltaX = e.clientX - dragStartPos.current.x
                const deltaY = e.clientY - dragStartPos.current.y

                const newX = Math.max(
                    0,
                    Math.min(window.innerWidth - 80, dragStartIconPos.current.x + deltaX)
                )
                const newY = Math.max(
                    0,
                    Math.min(window.innerHeight - 120, dragStartIconPos.current.y + deltaY)
                )

                setIconPositions((prev) => ({
                    ...prev,
                    [draggingIcon]: { x: newX, y: newY }
                }))
                return
            }

            // Handle selection box and determine which icons are inside
            if (!isSelectingRef.current || !desktopRef.current) return

            const rect = desktopRef.current.getBoundingClientRect()
            const currentX = e.clientX - rect.left
            const currentY = e.clientY - rect.top

            const startX = startPosRef.current.x
            const startY = startPosRef.current.y

            const x = Math.min(startX, currentX)
            const y = Math.min(startY, currentY)
            const width = Math.abs(currentX - startX)
            const height = Math.abs(currentY - startY)

            setSelectionBox({ x, y, width, height })

            // Determine which icons are inside the selection box
            if (width > 5 && height > 5) {
                const newSelected = new Set()
                icons.forEach((icon) => {
                    const pos = iconPositions[icon.id]
                    if (pos) {
                        const iconCenterX = pos.x + 48
                        const iconCenterY = pos.y + 46
                        if (
                            iconCenterX >= x &&
                            iconCenterX <= x + width &&
                            iconCenterY >= y &&
                            iconCenterY <= y + height
                        ) {
                            newSelected.add(icon.id)
                        }
                    }
                })
                setSelectedIcons(newSelected)
            }
        },
        [draggingIcon, icons, iconPositions]
    )

    // Handle mouse up - snap to grid
    const handleMouseUp = useCallback(() => {
        if (draggingIcon) {
            // Snap to grid on release
            const currentPos = iconPositions[draggingIcon]
            if (currentPos) {
                const snapped = snapToGrid(currentPos.x, currentPos.y)
                setIconPositions((prev) => ({
                    ...prev,
                    [draggingIcon]: snapped
                }))
            }
        }
        setDraggingIcon(null)
        isSelectingRef.current = false
        setSelectionBox(null)
    }, [draggingIcon, iconPositions])

    // Handle desktop mouse down (for selection box)
    const handleDesktopMouseDown = useCallback(
        (e) => {
            if (contextMenu) setContextMenu(null)
            if (iconContextMenu) setIconContextMenu(null)

            // Deselect all icons if clicking on desktop background
            if (!e.target.closest('.icon')) {
                setSelectedIcons(new Set())
            }

            // Don't start selection if clicking on an icon
            if (e.target.closest('.icon-wrapper')) return

            const rect = desktopRef.current.getBoundingClientRect()
            const x = e.clientX - rect.left
            const y = e.clientY - rect.top

            startPosRef.current = { x, y }
            isSelectingRef.current = true
            setSelectionBox({ x, y, width: 0, height: 0 })
        },
        [contextMenu, iconContextMenu]
    )

    const handleDesktopContextMenu = useCallback((e) => {
        e.preventDefault()

        // Only show desktop context menu if not clicking on an icon
        if (e.target.closest('.icon-wrapper')) return

        setIconContextMenu(null)
        setContextMenu({
            x: e.clientX,
            y: e.clientY
        })
    }, [])

    // Arrange icons in default grid
    const arrangeIcons = useCallback(() => {
        const newPositions = getDefaultPositions(icons)
        setIconPositions(newPositions)
        setContextMenu(null)
    }, [icons])

    // Line up icons - snap all to nearest grid position
    const lineUpIcons = useCallback(() => {
        const newPositions = {}
        icons.forEach((icon) => {
            const pos = iconPositions[icon.id]
            if (pos) {
                newPositions[icon.id] = snapToGrid(pos.x, pos.y)
            }
        })
        setIconPositions(newPositions)
        setContextMenu(null)
    }, [icons, iconPositions])

    const desktopMenuItems = [
        { label: 'Refresh', onClick: () => window.location.reload() },
        { type: 'separator' },
        { label: 'New Folder', disabled: true },
        { label: 'New Shortcut', disabled: true },
        { type: 'separator' },
        { label: 'Arrange Icons', onClick: arrangeIcons },
        { label: 'Line up Icons', onClick: lineUpIcons },
        { type: 'separator' },
        {
            label: 'Properties',
            onClick: () => {
                setContextMenu(null)
                onContextMenuAction?.('properties')
            }
        }
    ]

    const getIconMenuItems = (icon) => [
        {
            label: 'Open',
            onClick: () => {
                setIconContextMenu(null)
                onIconDoubleClick(icon.id)
            }
        },
        { type: 'separator' },
        { label: 'Cut', disabled: true },
        { label: 'Copy', disabled: true },
        { type: 'separator' },
        { label: 'Create Shortcut', disabled: true },
        { label: 'Delete', disabled: true },
        { label: 'Rename', disabled: true },
        { type: 'separator' },
        {
            label: 'Properties',
            onClick: () => {
                setIconContextMenu(null)
                setPropertiesDialog(getIconProperties(icon))
            }
        }
    ]

    // Ensure all icons have positions
    useEffect(() => {
        const newPositions = { ...iconPositions }
        let updated = false
        icons.forEach((icon) => {
            if (!newPositions[icon.id]) {
                const defaults = getDefaultPositions(icons)
                newPositions[icon.id] = defaults[icon.id]
                updated = true
            }
        })
        if (updated) {
            setIconPositions(newPositions)
        }
    }, [icons])

    return (
        <div
            className="desktop"
            ref={desktopRef}
            onMouseDown={handleDesktopMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            onMouseLeave={handleMouseUp}
            onContextMenu={handleDesktopContextMenu}
        >
            {/* Free-positioned icons */}
            {icons.map((icon) => {
                const pos = iconPositions[icon.id] || { x: 0, y: 0 }
                const isSelected = selectedIcons.has(icon.id)
                return (
                    <Tooltip key={icon.id} text={icon.label}>
                        <div
                            className={`icon-wrapper ${draggingIcon === icon.id ? 'dragging' : ''} ${isSelected ? 'selected' : ''}`}
                            style={{
                                position: 'absolute',
                                left: pos.x,
                                top: pos.y,
                                zIndex: draggingIcon === icon.id ? 1000 : 1
                            }}
                            onMouseDown={(e) => handleIconMouseDown(e, icon.id)}
                            onContextMenu={(e) => handleIconContextMenu(e, icon)}
                        >
                            <DesktopIcon
                                id={icon.id}
                                label={icon.label}
                                iconSrc={icon.icon}
                                onDoubleClick={() => onIconDoubleClick(icon.id)}
                                isSelected={isSelected}
                            />
                        </div>
                    </Tooltip>
                )
            })}

            {/* Selection box */}
            {selectionBox && selectionBox.width > 5 && selectionBox.height > 5 && (
                <div
                    className="selection-box"
                    style={{
                        left: selectionBox.x,
                        top: selectionBox.y,
                        width: selectionBox.width,
                        height: selectionBox.height
                    }}
                />
            )}

            {/* Desktop context menu */}
            {contextMenu && (
                <ContextMenu
                    x={contextMenu.x}
                    y={contextMenu.y}
                    items={desktopMenuItems}
                    onClose={() => setContextMenu(null)}
                />
            )}

            {/* Icon context menu */}
            {iconContextMenu && (
                <ContextMenu
                    x={iconContextMenu.x}
                    y={iconContextMenu.y}
                    items={getIconMenuItems(iconContextMenu.icon)}
                    onClose={() => setIconContextMenu(null)}
                />
            )}

            {/* Properties dialog */}
            {propertiesDialog && (
                <ErrorDialog
                    title={`${propertiesDialog.name} Properties`}
                    message={
                        <div style={{ textAlign: 'left', fontSize: '11px', lineHeight: '1.8' }}>
                            <p>
                                <strong>File Name:</strong> {propertiesDialog.name}
                            </p>
                            <p>
                                <strong>Path:</strong> {propertiesDialog.path}
                            </p>
                            <p>
                                <strong>Owner:</strong> {propertiesDialog.owner}
                            </p>
                        </div>
                    }
                    icon="info"
                    buttons={[
                        { label: 'OK', onClick: () => setPropertiesDialog(null), primary: true }
                    ]}
                    onClose={() => setPropertiesDialog(null)}
                />
            )}
        </div>
    )
}

