}

function FileExplorer({ onOpenWindow }) {
    const [currentPath, setCurrentPath] = useState('C:\\Desktop')
    const [selectedPath, setSelectedPath] = useState('C:\\Desktop')
    const [expandedPaths, setExpandedPaths] = useState([
        'C:',
        'C:\\Projects',
        'C:\\Projects\\Python'
    ])
    const [history, setHistory] = useState(['C:\\Desktop'])
    const [historyIndex, setHistoryIndex] = useState(0)
    const [addressInput, setAddressInput] = useState('C:\\Desktop')
    const [sidebarWidth, setSidebarWidth] = useState(180)

    const sidebarRef = useRef(null)
    const isResizing = useRef(false)

    // Sidebar resize handler
    const handleResizeMouseDown = useCallback((e) => {
        e.preventDefault()
        isResizing.current = true
        document.body.style.cursor = 'ew-resize'
        document.body.style.userSelect = 'none'
    }, [])

    useEffect(() => {
        const handleMouseMove = (e) => {
            if (!isResizing.current || !sidebarRef.current) return
            const sidebarRect = sidebarRef.current.getBoundingClientRect()
            const newWidth = e.clientX - sidebarRect.left
            if (newWidth >= 100 && newWidth <= 400) {
                setSidebarWidth(newWidth)
            }
        }

        const handleMouseUp = () => {
            isResizing.current = false
            document.body.style.cursor = ''
            document.body.style.userSelect = ''
        }

        document.addEventListener('mousemove', handleMouseMove)
        document.addEventListener('mouseup', handleMouseUp)
        return () => {
            document.removeEventListener('mousemove', handleMouseMove)
            document.removeEventListener('mouseup', handleMouseUp)
        }
    }, [])

    const toggleExpand = (path) => {
        setExpandedPaths((prev) =>
            prev.includes(path) ? prev.filter((p) => p !== path) : [...prev, path]
        )
    }

    // Get item at a specific path (moved up for use in navigation)
    const getItemAtPath = (path) => {
        const parts = path.split('\\').filter((p) => p)
        let current = FILE_SYSTEM[parts[0]]
        for (let i = 1; i < parts.length && current; i++) {
            current = current.children?.[parts[i]]
        }
        return current
    }

    const navigateTo = (path, item) => {
        if (item?.type === 'folder' || item?.type === 'drive') {
            setCurrentPath(path)
            setAddressInput(path)
            // Add to history
            const newHistory = history.slice(0, historyIndex + 1)
            newHistory.push(path)
            setHistory(newHistory)
            setHistoryIndex(newHistory.length - 1)
        }
        setSelectedPath(path)
    }

    // Handle address bar Enter key
    const handleAddressKeyDown = (e) => {
        if (e.key === 'Enter') {
            const item = getItemAtPath(addressInput)
            if (item && (item.type === 'folder' || item.type === 'drive')) {
                navigateTo(addressInput, item)
                // Expand path in tree
                const parts = addressInput.split('\\')
                const pathsToExpand = []
                for (let i = 1; i <= parts.length; i++) {
                    pathsToExpand.push(parts.slice(0, i).join('\\'))
                }
                setExpandedPaths((prev) => [...new Set([...prev, ...pathsToExpand])])
            }
        }
    }

    // Handle tree item double-click
    const handleTreeDoubleClick = (path, item) => {
        if (item.type === 'folder' || item.type === 'drive') {
            navigateTo(path, item)
        } else if (item.type === 'project' && item.id && onOpenWindow) {
            // Open project in project viewer window
            onOpenWindow(`project_${item.id.replace(/-/g, '')}`)
        } else if (item.windowId && onOpenWindow) {
            onOpenWindow(item.windowId)
        }
    }

    const goBack = () => {
        if (historyIndex > 0) {
            setHistoryIndex(historyIndex - 1)
            setCurrentPath(history[historyIndex - 1])
            setSelectedPath(history[historyIndex - 1])
        }
    }

    const goForward = () => {
        if (historyIndex < history.length - 1) {
            setHistoryIndex(historyIndex + 1)
            setCurrentPath(history[historyIndex + 1])
            setSelectedPath(history[historyIndex + 1])
        }
    }

    const goUp = () => {
        const parts = currentPath.split('\\')
        if (parts.length > 1) {
            const parentPath = parts.slice(0, -1).join('\\') || 'C:'
            const parentItem = getItemAtPath(parentPath)
            navigateTo(parentPath, parentItem)
        }
    }

    const currentItem = getItemAtPath(currentPath)
    const currentChildren = currentItem?.children || {}

    const handleItemDoubleClick = (item, key) => {
        const newPath = `${currentPath}\\${key}`

        if (item.type === 'folder') {
            navigateTo(newPath, item)
            if (!expandedPaths.includes(newPath)) {
                setExpandedPaths([...expandedPaths, newPath])
            }
        } else if (item.windowId && onOpenWindow) {
            onOpenWindow(item.windowId)
        } else if (item.type === 'project' && onOpenWindow) {
            // Open project wizard window - normalize ID (remove hyphens)
            const normalizedId = item.id.replace(/-/g, '')
            onOpenWindow('project_' + normalizedId)
        }
    }

    // Check if selected item is a project
    const selectedItem = getItemAtPath(selectedPath)

    return (
        <div className="file-explorer">
            <div className="file-explorer-toolbar">
                <button
                    className="file-explorer-toolbar-btn"
                    onClick={goBack}
                    disabled={historyIndex === 0}
                    title="Back"
                >
                    ‚Üê
                </button>
                <button
                    className="file-explorer-toolbar-btn"
                    onClick={goForward}
                    disabled={historyIndex >= history.length - 1}
                    title="Forward"
                >
                    ‚Üí
                </button>
                <button
                    className="file-explorer-toolbar-btn"
                    onClick={goUp}
                    disabled={currentPath === 'C:'}
                    title="Up One Level"
                >
                    ‚Üë
                </button>
            </div>
            <div className="file-explorer-address">
                <span>Address:</span>
                <input
                    type="text"
                    className="file-explorer-address-input"
                    value={addressInput}
                    onChange={(e) => setAddressInput(e.target.value)}
                    onKeyDown={handleAddressKeyDown}
                    onBlur={() => setAddressInput(currentPath)}
                />
                <button
                    className="file-explorer-toolbar-btn"
                    onClick={() => {
                        const item = getItemAtPath(addressInput)
                        if (item && (item.type === 'folder' || item.type === 'drive')) {
                            navigateTo(addressInput, item)
                        }
                    }}
                    title="Go"
                >
                    ‚Üí
                </button>
            </div>
            <div className="file-explorer-main">
                <div
                    className="file-explorer-sidebar"
                    ref={sidebarRef}
                    style={{ width: sidebarWidth }}
                >
                    {Object.entries(FILE_SYSTEM).map(([key, item]) => (
                        <TreeItem
                            key={key}
                            item={item}
                            path={key}
                            selectedPath={selectedPath}
                            onSelect={navigateTo}
                            expandedPaths={expandedPaths}
                            onToggle={toggleExpand}
                            onDoubleClick={handleTreeDoubleClick}
                        />
                    ))}
                    <div
                        className="file-explorer-resize-handle"
                        onMouseDown={handleResizeMouseDown}
                    />
                </div>
                <div className="file-explorer-content">
                    <div className="file-grid">
                        {Object.entries(currentChildren).map(([key, item]) => (
                            <FileItem
                                key={key}
                                item={item}
                                itemKey={key}
                                onDoubleClick={handleItemDoubleClick}
                            />
                        ))}
                        {Object.keys(currentChildren).length === 0 && (
                            <div style={{ padding: 20, color: '#808080' }}>
                                This folder is empty.
                            </div>
                        )}
                    </div>
                </div>
            </div>
            <div className="file-explorer-status">
                {Object.keys(currentChildren).length} object(s)
            </div>
        </div>
    )
}

export default FileExplorer

import { useState, useEffect } from 'react'

// Get base path for sounds (dev vs prod)
const getBasePath = () => {
    if (window.location.pathname.includes('/Portfolio')) {
        return '/Portfolio'
    }
    return ''
}

function LoginScreen({ onComplete }) {
    const [phase, setPhase] = useState('welcome') // welcome, logging, complete
    const [progress, setProgress] = useState(0)
    const [audioPlayed, setAudioPlayed] = useState(false)

    // Play startup sound when component mounts
    useEffect(() => {
        if (!audioPlayed) {
            const basePath = getBasePath()
            const audio = new Audio(`${basePath}/sounds/startup.wav`)
            audio.volume = 0.5
            audio.play().catch(() => {})
            setAudioPlayed(true)
        }
    }, [audioPlayed])

    // Progress animation - runs for about 4 seconds (100 steps at 40ms = 4s)
    useEffect(() => {
        if (phase === 'logging') {
            const interval = setInterval(() => {
                setProgress((prev) => {
                    if (prev >= 100) {
                        clearInterval(interval)
                        setPhase('complete')
                        return 100
                    }
                    return prev + 1
                })
            }, 40) // 100 steps * 40ms = 4 seconds
            return () => clearInterval(interval)
        }
    }, [phase])

    // Welcome phase - show for 1.5 seconds
    useEffect(() => {
        if (phase === 'welcome') {
            const timer = setTimeout(() => setPhase('logging'), 1500)
            return () => clearTimeout(timer)
        }
    }, [phase])

    // Complete phase - show for 0.5 seconds then transition
    // Total: 1.5s welcome + 4s logging + 0.5s complete = 6 seconds (matches startup.wav)
    useEffect(() => {
        if (phase === 'complete') {
            const timer = setTimeout(() => {
                if (onComplete) onComplete()
            }, 500)
            return () => clearTimeout(timer)
        }
    }, [phase, onComplete])

    return (
        <div className="login-screen">
            <div className="login-box">
                <div className="login-header">
                    <img src="icons/computer.svg" alt="" className="login-logo" />
                    <h2>BorgesOS</h2>
                </div>

                {phase === 'welcome' && (
                    <div className="login-content">
                        <p className="login-welcome">Welcome</p>
                    </div>
                )}

                {phase === 'logging' && (
                    <div className="login-content">
                        <p className="login-status">Logging on...</p>
                        <div className="login-progress">
                            <div className="login-progress-bar" style={{ width: `${progress}%` }} />
                        </div>
                    </div>
                )}

                {phase === 'complete' && (
                    <div className="login-content">
                        <p className="login-status">Loading personal settings...</p>
                    </div>
                )}
            </div>
        </div>
    )
}

export default LoginScreen

import { useState, useEffect, useCallback, useRef } from 'react'
import '../index.css'

const DIFFICULTIES = {
    beginner: { rows: 9, cols: 9, mines: 10 },
    intermediate: { rows: 16, cols: 16, mines: 40 },
    expert: { rows: 16, cols: 30, mines: 99 }
}

const CELL_STATE = {
    HIDDEN: 0,
    REVEALED: 1,
    FLAGGED: 2,
    QUESTION: 3 // Optional, classic feature
}

const GAME_STATE = {
    IDLE: 'idle',
    PLAYING: 'playing',
    WON: 'won',
    LOST: 'lost'
}

const Minesweeper = ({ onResize }) => {
    const [difficulty, setDifficulty] = useState('beginner')
    const [grid, setGrid] = useState([])
    const [gameState, setGameState] = useState(GAME_STATE.IDLE)
    const [minesLeft, setMinesLeft] = useState(0)
    const [time, setTime] = useState(0)
    const [isMouseDown, setIsMouseDown] = useState(false)
    const timerRef = useRef(null)

    // Initialize game
    const initGame = useCallback(
        (diff = difficulty) => {
            const { rows, cols, mines } = DIFFICULTIES[diff]

            // Calculate needed window size
            // Cell is 16px + 3px border (approx 20px?)
            // Actually CSS says 16px width/height + 2px border?
            // Let's estimate: 16px cell * cols + borders + padding
            // Header height is approx 50-60px
            // Frame borders (3px + 3px)

            // Exact calculation:
            // Borders: 3px (window body) + 6px (game area padding) + 6px (inset border) = 15px width overhead
            // Cells: 16px * cols
            // Width = 16 * cols + 30 (adjust for safety/margins)

            // Height:
            // Header: approx 50px
            // Board: 16px * rows
            // Borders: similar overhead
            // Height = 16 * rows + 80

            const neededWidth = cols * 16 + 40
            const neededHeight = rows * 16 + 100

            if (onResize) {
                onResize(neededWidth, neededHeight)
            }

            // Create empty grid
            const newGrid = Array(rows)
                .fill(null)
                .map(() =>
                    Array(cols)
                        .fill(null)
                        .map(() => ({
                            isMine: false,
                            state: CELL_STATE.HIDDEN,
                            neighborCount: 0
                        }))
                )

            setGrid(newGrid)
            setGameState(GAME_STATE.IDLE)
            setMinesLeft(mines)
            setTime(0)
            clearInterval(timerRef.current)
        },
        [difficulty, onResize]
    )

    useEffect(() => {
        initGame()
        return () => clearInterval(timerRef.current)
    }, [initGame])

    // Place mines (on first click to ensure safety)
    const placeMines = (firstRow, firstCol) => {
        const { rows, cols, mines } = DIFFICULTIES[difficulty]
        const newGrid = [...grid] // Shallow copy of rows
        // Deep copy needed for mutation during setup? Actually, mapping is safer.
        // Let's just mutate the logic for placement since we are replacing state anyway.
        // But for React state safety, we should clone.
        const gridClone = newGrid.map((row) => row.map((cell) => ({ ...cell })))

        let minesPlaced = 0
        while (minesPlaced < mines) {
            const r = Math.floor(Math.random() * rows)
            const c = Math.floor(Math.random() * cols)

            // Don't place mine on first clicked cell or neighbors
            if (!gridClone[r][c].isMine && stateIsSafe(r, c, firstRow, firstCol)) {
                gridClone[r][c].isMine = true
                minesPlaced++
            }
        }

        // Calculate neighbors
        for (let r = 0; r < rows; r++) {
            for (let c = 0; c < cols; c++) {
                if (!gridClone[r][c].isMine) {
                    let count = 0
                    for (let i = -1; i <= 1; i++) {
                        for (let j = -1; j <= 1; j++) {
                            const nr = r + i
                            const nc = c + j
                            if (
                                nr >= 0 &&
                                nr < rows &&
                                nc >= 0 &&
                                nc < cols &&
                                gridClone[nr][nc].isMine
                            ) {
                                count++
                            }
                        }
                    }
                    gridClone[r][c].neighborCount = count
                }
            }
        }

        return gridClone
    }

    const stateIsSafe = (r, c, firstRow, firstCol) => {
        // Safe if not the clicked cell (and maybe neighbors for strictly fair start,
        // but standard usually just guarantees the cell itself is not a mine, or is a 0)
        // Windows 95 moves the mine to the first available safe spot if you click one.
        // Simplified: Ensure first click is not a mine.
        if (r === firstRow && c === firstCol) return false
        return true
    }

    const startGame = (r, c) => {
        const newGrid = placeMines(r, c)
        setGrid(newGrid)
        setGameState(GAME_STATE.PLAYING)

        // Start timer
        timerRef.current = setInterval(() => {
            setTime((prev) => Math.min(prev + 1, 999))
        }, 1000)

        // Reveal the first clicked cell
        revealCell(newGrid, r, c)
    }

    const revealCell = (currentGrid, r, c) => {
        if (
            currentGrid[r][c].state !== CELL_STATE.HIDDEN &&
            currentGrid[r][c].state !== CELL_STATE.QUESTION
        )
            return

        if (currentGrid[r][c].isMine) {
            // Game Over
            gameOver(currentGrid)
            return
        }

        const newGrid = [...currentGrid] // We are mutating the passed grid reference if it was cloned already
        // But for recursive revealing, we need to operate on the same data structure

        // Check recursion depth/performance - stack size should be fine for 30x16

        const queue = [[r, c]]

        while (queue.length > 0) {
            const [currR, currC] = queue.pop()

            if (newGrid[currR][currC].state === CELL_STATE.REVEALED) continue

            newGrid[currR][currC].state = CELL_STATE.REVEALED

            // If empty cell, reveal neighbors
            if (newGrid[currR][currC].neighborCount === 0) {
                for (let i = -1; i <= 1; i++) {
                    for (let j = -1; j <= 1; j++) {
                        const nr = currR + i
                        const nc = currC + j
                        if (
                            nr >= 0 &&
                            nr < DIFFICULTIES[difficulty].rows &&
                            nc >= 0 &&
                            nc < DIFFICULTIES[difficulty].cols &&
                            newGrid[nr][nc].state === CELL_STATE.HIDDEN
                        ) {
                            queue.push([nr, nc])
                        }
                    }
                }
            }
        }

        setGrid(newGrid)
        checkWin(newGrid)
    }

    const handleCellClick = (r, c) => {
        if (gameState === GAME_STATE.WON || gameState === GAME_STATE.LOST) return
        if (grid[r][c].state === CELL_STATE.FLAGGED) return

        if (gameState === GAME_STATE.IDLE) {
            startGame(r, c)
        } else {
            // Deep clone to modify state
            const newGrid = grid.map((row) => row.map((cell) => ({ ...cell })))
            if (newGrid[r][c].isMine) {
                newGrid[r][c].isExploded = true // Mark specifically which mine exploded
                setGrid(newGrid)
                gameOver(newGrid)
            } else {
                revealCell(newGrid, r, c)
            }
        }
    }

    const handleCellRightClick = (e, r, c) => {
        e.preventDefault()
        if (gameState === GAME_STATE.WON || gameState === GAME_STATE.LOST) return
        if (grid[r][c].state === CELL_STATE.REVEALED) return

        const newGrid = grid.map((row) => row.map((cell) => ({ ...cell })))
        const cell = newGrid[r][c]

        if (cell.state === CELL_STATE.HIDDEN) {
            cell.state = CELL_STATE.FLAGGED
            setMinesLeft((prev) => prev - 1)
        } else if (cell.state === CELL_STATE.FLAGGED) {
            cell.state = CELL_STATE.QUESTION
            setMinesLeft((prev) => prev + 1)
        } else if (cell.state === CELL_STATE.QUESTION) {
            cell.state = CELL_STATE.HIDDEN
        }

        setGrid(newGrid)
    }

    const gameOver = (finalGrid) => {
        setGameState(GAME_STATE.LOST)
        clearInterval(timerRef.current)

        // Reveal all mines usually
        const newGrid = finalGrid.map((row) =>
            row.map((cell) => {
                if (cell.isMine && cell.state !== CELL_STATE.FLAGGED) {
                    return { ...cell, state: CELL_STATE.REVEALED }
                }
                if (!cell.isMine && cell.state === CELL_STATE.FLAGGED) {
                    return { ...cell, isMisflagged: true }
                }
                return cell
            })
        )
        setGrid(newGrid)
    }

    const checkWin = (currentGrid) => {
        const { rows, cols, mines } = DIFFICULTIES[difficulty]
        let hiddenCount = 0
        for (let r = 0; r < rows; r++) {
            for (let c = 0; c < cols; c++) {
                if (currentGrid[r][c].state !== CELL_STATE.REVEALED) {
                    hiddenCount++
                }
            }
        }

        if (hiddenCount === mines) {
            setGameState(GAME_STATE.WON)
            clearInterval(timerRef.current)
            setMinesLeft(0)
            // Flag remaining mines
            const newGrid = currentGrid.map((row) =>
                row.map((cell) => {
                    if (cell.isMine) {
                        return { ...cell, state: CELL_STATE.FLAGGED }
                    }
                    return cell
                })
            )
            setGrid(newGrid)
        }
    }

    // Smiley face based on state
    const getSmiley = () => {
        if (gameState === GAME_STATE.WON) return 'üòé'
        if (gameState === GAME_STATE.LOST) return 'üòµ'
        if (isMouseDown) return 'üòÆ'
        return 'üôÇ'
    }

    const changeDifficulty = (diff) => {
        setDifficulty(diff)
        // initGame happens in useEffect when difficulty changes
    }

    // Number display helper
    const formatNumber = (num) => {
        if (num < -99) return '-99'
        if (num > 999) return '999'
        if (num < 0) return '-' + Math.abs(num).toString().padStart(2, '0')
        return num.toString().padStart(3, '0')
    }

    return (
        <div className="minesweeper-container">
            <div className="minesweeper-menubar">
                <div className="minesweeper-menu-item">
                    Game
                    <div className="minesweeper-submenu">
                        <div onClick={() => initGame()}>New</div>
                        <div className="menu-separator"></div>
                        <div onClick={() => changeDifficulty('beginner')}>Beginner</div>
                        <div onClick={() => changeDifficulty('intermediate')}>Intermediate</div>
                        <div onClick={() => changeDifficulty('expert')}>Expert</div>
                        <div className="menu-separator"></div>
                        <div onClick={() => window.close()}>Exit</div>
                    </div>
                </div>
                <div className="minesweeper-menu-item">Help</div>
            </div>

            <div className="minesweeper-game-area">
                <div className="minesweeper-header">
                    <div className="minesweeper-counter">{formatNumber(minesLeft)}</div>
                    <button className="minesweeper-face-btn" onClick={() => initGame()}>
                        {getSmiley()}
                    </button>
                    <div className="minesweeper-counter">{formatNumber(time)}</div>
                </div>

                <div
                    className={`minesweeper-grid ${difficulty}`}
                    onMouseDown={() => gameState === GAME_STATE.PLAYING && setIsMouseDown(true)}
                    onMouseUp={() => setIsMouseDown(false)}
                    onMouseLeave={() => setIsMouseDown(false)}
                >
                    {grid.map((row, r) => (
                        <div key={r} className="minesweeper-row">
                            {row.map((cell, c) => (
                                <div
                                    key={c}
                                    className={`minesweeper-cell ${
                                        cell.state === CELL_STATE.REVEALED ? 'revealed' : ''
                                    } ${
                                        cell.state === CELL_STATE.REVEALED && cell.neighborCount > 0
                                            ? `n${cell.neighborCount}`
                                            : ''
                                    }`}
                                    onClick={() => handleCellClick(r, c)}
                                    onContextMenu={(e) => handleCellRightClick(e, r, c)}
                                >
                                    {cell.state === CELL_STATE.FLAGGED && 'üö©'}
                                    {cell.state === CELL_STATE.QUESTION && '?'}
                                    {cell.state === CELL_STATE.REVEALED && cell.isMine && 'üí£'}
                                    {cell.state === CELL_STATE.REVEALED &&
                                        !cell.isMine &&
                                        cell.neighborCount > 0 &&
                                        cell.neighborCount}
                                    {cell.isExploded && <span className="explosion">üí•</span>}
                                    {cell.isMisflagged && <span className="misflag">‚ùå</span>}
                                </div>
                            ))}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    )
}

export default Minesweeper

import { useState, useRef, useEffect, useCallback } from 'react'

const TOOLS = {
    PENCIL: 'pencil',
    BRUSH: 'brush',
    ERASER: 'eraser',
    LINE: 'line',
    RECTANGLE: 'rectangle',
    ELLIPSE: 'ellipse',
    FILL: 'fill',
    EYEDROPPER: 'eyedropper',
    TEXT: 'text'
}

const COLOR_PALETTE = [
    '#000000',
    '#808080',
    '#800000',
    '#808000',
    '#008000',
    '#008080',
    '#000080',
    '#800080',
    '#ffffff',
    '#c0c0c0',
    '#ff0000',
    '#ffff00',
    '#00ff00',
    '#00ffff',
    '#0000ff',
    '#ff00ff',
    '#c0dcc0',
    '#a6caf0',
    '#fffbf0',
    '#a0a0a4',
    '#ff8040',
    '#00ff80',
    '#80ffff',
    '#8080ff',
    '#ff0080',
    '#804040',
    '#408040',
    '#004080',
    '#408080',
    '#804080',
    '#ff8080',
    '#80ff80'
]

const BRUSH_SIZES = [1, 2, 4, 6, 8, 12, 16, 24]

function Paint() {
    const canvasRef = useRef(null)
    const overlayCanvasRef = useRef(null)
    const [isDrawing, setIsDrawing] = useState(false)
    const [tool, setTool] = useState(TOOLS.PENCIL)
    const [color, setColor] = useState('#000000')
    const [secondaryColor, setSecondaryColor] = useState('#ffffff')
    const [brushSize, setBrushSize] = useState(2)
    const [startPos, setStartPos] = useState({ x: 0, y: 0 })
    const [history, setHistory] = useState([])
    const [historyIndex, setHistoryIndex] = useState(-1)
    const [cursorPos, setCursorPos] = useState({ x: 0, y: 0 })

    // Initialize canvas
    useEffect(() => {
        const canvas = canvasRef.current
        const overlay = overlayCanvasRef.current
        if (!canvas || !overlay) return

        const ctx = canvas.getContext('2d')
        ctx.fillStyle = '#ffffff'
        ctx.fillRect(0, 0, canvas.width, canvas.height)

        // Save initial state
        saveToHistory()
    }, [])

    // Save canvas state to history
    const saveToHistory = useCallback(() => {
        const canvas = canvasRef.current
        if (!canvas) return

        const imageData = canvas.toDataURL()
        setHistory((prev) => {
            const newHistory = prev.slice(0, historyIndex + 1)
            newHistory.push(imageData)
            return newHistory.slice(-20) // Keep last 20 states
        })
        setHistoryIndex((prev) => Math.min(prev + 1, 19))
    }, [historyIndex])

    // Undo
    const undo = useCallback(() => {
        if (historyIndex > 0) {
            const canvas = canvasRef.current
            const ctx = canvas.getContext('2d')
            const img = new Image()
            img.onload = () => {
                ctx.clearRect(0, 0, canvas.width, canvas.height)
                ctx.drawImage(img, 0, 0)
            }
            img.src = history[historyIndex - 1]
            setHistoryIndex((prev) => prev - 1)
        }
    }, [history, historyIndex])

    // Redo
    const redo = useCallback(() => {
        if (historyIndex < history.length - 1) {
            const canvas = canvasRef.current
            const ctx = canvas.getContext('2d')
            const img = new Image()
            img.onload = () => {
                ctx.clearRect(0, 0, canvas.width, canvas.height)
                ctx.drawImage(img, 0, 0)
            }
            img.src = history[historyIndex + 1]
            setHistoryIndex((prev) => prev + 1)
        }
    }, [history, historyIndex])

    // Clear canvas
    const clearCanvas = useCallback(() => {
        const canvas = canvasRef.current
        const ctx = canvas.getContext('2d')
        ctx.fillStyle = secondaryColor
        ctx.fillRect(0, 0, canvas.width, canvas.height)
        saveToHistory()
    }, [secondaryColor, saveToHistory])

    // Get mouse position relative to canvas
    const getMousePos = (e) => {
        const canvas = canvasRef.current
        const rect = canvas.getBoundingClientRect()
        const scaleX = canvas.width / rect.width
        const scaleY = canvas.height / rect.height
        return {
            x: Math.floor((e.clientX - rect.left) * scaleX),
            y: Math.floor((e.clientY - rect.top) * scaleY)
        }
    }

    // Drawing functions
    const drawLine = (ctx, x1, y1, x2, y2, strokeColor, size) => {
        ctx.beginPath()
        ctx.strokeStyle = strokeColor
        ctx.lineWidth = size
        ctx.lineCap = 'round'
        ctx.lineJoin = 'round'
        ctx.moveTo(x1, y1)
        ctx.lineTo(x2, y2)
        ctx.stroke()
    }

    const drawRectangle = (ctx, x1, y1, x2, y2, strokeColor, size, fill = false) => {
        const x = Math.min(x1, x2)
        const y = Math.min(y1, y2)
        const w = Math.abs(x2 - x1)
        const h = Math.abs(y2 - y1)

        ctx.beginPath()
        ctx.strokeStyle = strokeColor
        ctx.lineWidth = size
        ctx.rect(x, y, w, h)
        if (fill) {
            ctx.fillStyle = strokeColor
            ctx.fill()
        }
        ctx.stroke()
    }

    const drawEllipse = (ctx, x1, y1, x2, y2, strokeColor, size, fill = false) => {
        const centerX = (x1 + x2) / 2
        const centerY = (y1 + y2) / 2
        const radiusX = Math.abs(x2 - x1) / 2
        const radiusY = Math.abs(y2 - y1) / 2

        ctx.beginPath()
        ctx.strokeStyle = strokeColor
        ctx.lineWidth = size
        ctx.ellipse(centerX, centerY, radiusX, radiusY, 0, 0, 2 * Math.PI)
        if (fill) {
            ctx.fillStyle = strokeColor
            ctx.fill()
        }
        ctx.stroke()
    }

    // Flood fill algorithm
    const floodFill = (startX, startY, fillColor) => {
        const canvas = canvasRef.current
        const ctx = canvas.getContext('2d')
        const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height)
        const data = imageData.data

        const startIdx = (startY * canvas.width + startX) * 4
        const startR = data[startIdx]
        const startG = data[startIdx + 1]
        const startB = data[startIdx + 2]

        // Parse fill color
        const hex = fillColor.replace('#', '')
        const fillR = parseInt(hex.substring(0, 2), 16)
        const fillG = parseInt(hex.substring(2, 4), 16)
        const fillB = parseInt(hex.substring(4, 6), 16)

        // If same color, return
        if (startR === fillR && startG === fillG && startB === fillB) return

        const stack = [[startX, startY]]
        const visited = new Set()

        while (stack.length > 0) {
            const [x, y] = stack.pop()
            const key = `${x},${y}`

            if (visited.has(key)) continue
            if (x < 0 || x >= canvas.width || y < 0 || y >= canvas.height) continue

            const idx = (y * canvas.width + x) * 4
            if (data[idx] !== startR || data[idx + 1] !== startG || data[idx + 2] !== startB)
                continue

            visited.add(key)
            data[idx] = fillR
            data[idx + 1] = fillG
            data[idx + 2] = fillB
            data[idx + 3] = 255

            stack.push([x + 1, y], [x - 1, y], [x, y + 1], [x, y - 1])
        }

        ctx.putImageData(imageData, 0, 0)
    }

    // Eyedropper
    const pickColor = (x, y) => {
        const canvas = canvasRef.current
        const ctx = canvas.getContext('2d')
        const pixel = ctx.getImageData(x, y, 1, 1).data
        const hex =
            '#' +
            [pixel[0], pixel[1], pixel[2]].map((c) => c.toString(16).padStart(2, '0')).join('')
        setColor(hex)
    }

    // Mouse event handlers
    const handleMouseDown = (e) => {
        const pos = getMousePos(e)
        setStartPos(pos)
        setIsDrawing(true)

        if (tool === TOOLS.FILL) {
            floodFill(pos.x, pos.y, e.button === 2 ? secondaryColor : color)
            saveToHistory()
        } else if (tool === TOOLS.EYEDROPPER) {
            pickColor(pos.x, pos.y)
        } else if (tool === TOOLS.PENCIL || tool === TOOLS.BRUSH || tool === TOOLS.ERASER) {
            const canvas = canvasRef.current
            const ctx = canvas.getContext('2d')
            const drawColor =
                tool === TOOLS.ERASER ? secondaryColor : e.button === 2 ? secondaryColor : color
            const size = tool === TOOLS.BRUSH ? brushSize * 2 : brushSize

            ctx.beginPath()
            ctx.arc(pos.x, pos.y, size / 2, 0, 2 * Math.PI)
            ctx.fillStyle = drawColor
            ctx.fill()
        }
    }

    const handleMouseMove = (e) => {
        const pos = getMousePos(e)
        setCursorPos(pos)

        if (!isDrawing) return

        const canvas = canvasRef.current
        const overlay = overlayCanvasRef.current
        const ctx = canvas.getContext('2d')
        const overlayCtx = overlay.getContext('2d')

        const drawColor = tool === TOOLS.ERASER ? secondaryColor : color
        const size = tool === TOOLS.BRUSH ? brushSize * 2 : brushSize

        if (tool === TOOLS.PENCIL || tool === TOOLS.BRUSH || tool === TOOLS.ERASER) {
            drawLine(ctx, startPos.x, startPos.y, pos.x, pos.y, drawColor, size)
            setStartPos(pos)
        } else if (tool === TOOLS.LINE || tool === TOOLS.RECTANGLE || tool === TOOLS.ELLIPSE) {
            // Clear overlay and draw preview
            overlayCtx.clearRect(0, 0, overlay.width, overlay.height)

            if (tool === TOOLS.LINE) {
                drawLine(overlayCtx, startPos.x, startPos.y, pos.x, pos.y, drawColor, size)
            } else if (tool === TOOLS.RECTANGLE) {
                drawRectangle(overlayCtx, startPos.x, startPos.y, pos.x, pos.y, drawColor, size)
            } else if (tool === TOOLS.ELLIPSE) {
                drawEllipse(overlayCtx, startPos.x, startPos.y, pos.x, pos.y, drawColor, size)
            }
        }
    }

    const handleMouseUp = (e) => {
        if (!isDrawing) return

        const pos = getMousePos(e)
        const canvas = canvasRef.current
        const overlay = overlayCanvasRef.current
        const ctx = canvas.getContext('2d')
        const overlayCtx = overlay.getContext('2d')

        const drawColor = tool === TOOLS.ERASER ? secondaryColor : color
        const size = tool === TOOLS.BRUSH ? brushSize * 2 : brushSize

        // Finalize shape tools
        if (tool === TOOLS.LINE) {
            drawLine(ctx, startPos.x, startPos.y, pos.x, pos.y, drawColor, size)
        } else if (tool === TOOLS.RECTANGLE) {
            drawRectangle(ctx, startPos.x, startPos.y, pos.x, pos.y, drawColor, size)
        } else if (tool === TOOLS.ELLIPSE) {
            drawEllipse(ctx, startPos.x, startPos.y, pos.x, pos.y, drawColor, size)
        }

        // Clear overlay
        overlayCtx.clearRect(0, 0, overlay.width, overlay.height)

        setIsDrawing(false)

        if (tool !== TOOLS.EYEDROPPER && tool !== TOOLS.FILL) {
            saveToHistory()
        }
    }

    // Save image
    const saveImage = () => {
        const canvas = canvasRef.current
        const link = document.createElement('a')
        link.download = 'my-painting.png'
        link.href = canvas.toDataURL()
        link.click()
    }

    // Tool button component
    const ToolButton = ({ toolType, icon, title }) => (
        <button
            className={`paint-tool-btn ${tool === toolType ? 'active' : ''}`}
            onClick={() => setTool(toolType)}
            title={title}
        >
            {icon}
        </button>
    )

    return (
        <div className="paint-container">
            {/* Menu bar */}
            <div className="paint-menubar">
                <div className="paint-menu-item">
                    <span className="paint-menu-label">File</span>
                    <div className="paint-dropdown">
                        <button onClick={clearCanvas}>New</button>
                        <button onClick={saveImage}>Save As...</button>
                    </div>
                </div>
                <div className="paint-menu-item">
                    <span className="paint-menu-label">Edit</span>
                    <div className="paint-dropdown">
                        <button onClick={undo} disabled={historyIndex <= 0}>
                            Undo
                        </button>
                        <button onClick={redo} disabled={historyIndex >= history.length - 1}>
                            Redo
                        </button>
                    </div>
                </div>
            </div>

            <div className="paint-main">
                {/* Toolbar */}
                <div className="paint-toolbar">
                    <ToolButton toolType={TOOLS.PENCIL} icon="‚úèÔ∏è" title="Pencil" />
                    <ToolButton toolType={TOOLS.BRUSH} icon="üñåÔ∏è" title="Brush" />
                    <ToolButton toolType={TOOLS.ERASER} icon="üßπ" title="Eraser" />
                    <ToolButton toolType={TOOLS.FILL} icon="ü™£" title="Fill" />
                    <ToolButton toolType={TOOLS.EYEDROPPER} icon="üíâ" title="Eyedropper" />
                    <div className="paint-tool-separator" />
                    <ToolButton toolType={TOOLS.LINE} icon="üìè" title="Line" />
                    <ToolButton toolType={TOOLS.RECTANGLE} icon="‚¨ú" title="Rectangle" />
                    <ToolButton toolType={TOOLS.ELLIPSE} icon="‚≠ï" title="Ellipse" />

                    <div className="paint-tool-separator" />

                    {/* Brush size */}
                    <div className="paint-size-selector">
                        {BRUSH_SIZES.slice(0, 4).map((size) => (
                            <button
                                key={size}
                                className={`paint-size-btn ${brushSize === size ? 'active' : ''}`}
                                onClick={() => setBrushSize(size)}
                                title={`Size ${size}`}
                            >
                                <span
                                    style={{
                                        width: Math.min(size * 2, 16),
                                        height: Math.min(size * 2, 16),
                                        background: '#000',
                                        borderRadius: '50%',
                                        display: 'inline-block'
                                    }}
                                />
                            </button>
                        ))}
                    </div>
                </div>

                {/* Canvas area */}
                <div className="paint-canvas-area">
                    <div className="paint-canvas-wrapper">
                        <canvas
                            ref={canvasRef}
                            width={640}
                            height={400}
                            className="paint-canvas"
                            onMouseDown={handleMouseDown}
                            onMouseMove={handleMouseMove}
                            onMouseUp={handleMouseUp}
                            onMouseLeave={handleMouseUp}
                            onContextMenu={(e) => e.preventDefault()}
                        />
                        <canvas
                            ref={overlayCanvasRef}
                            width={640}
                            height={400}
                            className="paint-overlay"
                            style={{ pointerEvents: 'none' }}
                        />
                    </div>
                </div>

                {/* Color palette */}
                <div className="paint-palette">
                    <div className="paint-current-colors">
                        <div
                            className="paint-color-display primary"
                            style={{ backgroundColor: color }}
                            title="Primary color (left click)"
                        />
                        <div
                            className="paint-color-display secondary"
                            style={{ backgroundColor: secondaryColor }}
                            title="Secondary color (right click)"
                        />
                    </div>
                    <div className="paint-colors">
                        {COLOR_PALETTE.map((c, i) => (
                            <button
                                key={i}
                                className={`paint-color-btn ${color === c ? 'active' : ''}`}
                                style={{ backgroundColor: c }}
                                onClick={() => setColor(c)}
                                onContextMenu={(e) => {
                                    e.preventDefault()
                                    setSecondaryColor(c)
                                }}
                                title={c}
                            />
                        ))}
                    </div>
                </div>
            </div>

            {/* Status bar */}
            <div className="paint-statusbar">
                <span>
                    Position: {cursorPos.x}, {cursorPos.y}
                </span>
                <span>Tool: {tool}</span>
                <span>Size: {brushSize}px</span>
            </div>
        </div>
    )
}

export default Paint

