        tech: ['Python', 'Math Algorithms', 'Optimization'],
        github: 'https://github.com/F3stive-Ya/CommonFactors',
        icon: 'icons/calculator-0.png',
        version: '1.2'
    }
}

const WIZARD_STEPS = {
    WELCOME: 0,
    LICENSE: 1, // Optional, renaming to "Requirements" to fit content
    INSTALLING: 2,
    FINISHED: 3
}

const ProjectViewer = ({ projectId, onClose, isOpen }) => {
    const project = PROJECTS_DATA[projectId]
    const [step, setStep] = useState(WIZARD_STEPS.WELCOME)
    const [progress, setProgress] = useState(0)
    const [installLog, setInstallLog] = useState([])

    // Reset state when project changes or window is re-opened
    useEffect(() => {
        if (isOpen) {
            setStep(WIZARD_STEPS.WELCOME)
            setProgress(0)
            setInstallLog([])
        }
    }, [projectId, isOpen])

    // Handle installation simulation
    useEffect(() => {
        if (step === WIZARD_STEPS.INSTALLING) {
            let p = 0
            const logs = [
                'Preparing installation...',
                'Copying files...',
                'Extracting assets...',
                'Configuring dependencies...',
                'Registering components...',
                'Optimizing performance...',
                'Cleaning up...',
                'Done.'
            ]

            const interval = setInterval(() => {
                p += Math.random() * 15
                if (p >= 100) {
                    p = 100
                    setStep(WIZARD_STEPS.FINISHED)
                    clearInterval(interval)
                }
                setProgress(Math.min(p, 100))

                // Add random log
                if (Math.random() > 0.7) {
                    const log = logs[Math.floor(Math.random() * logs.length)]
                    setInstallLog((prev) => [...prev.slice(-4), log])
                }
            }, 300)

            return () => clearInterval(interval)
        }
    }, [step])

    if (!project) return <div className="p-4">Project not found</div>

    const handleNext = () => {
        if (step < WIZARD_STEPS.FINISHED) {
            setStep((prev) => prev + 1)
        } else {
            // Launch (Open GitHub) and Close
            window.open(project.github, '_blank')
            if (onClose) onClose() // Assuming we might pass this prop, or user manually closes
        }
    }

    const handleBack = () => {
        if (step > WIZARD_STEPS.WELCOME) {
            setStep((prev) => prev - 1)
        }
    }

    return (
        <div className="wizard-container">
            <div className="wizard-sidebar">
                <img
                    src="icons/installer_generic_old-0.png"
                    alt="Setup"
                    className="wizard-sidebar-img"
                />
                <div className="wizard-sidebar-text">Borges OS Setup</div>
            </div>

            <div className="wizard-content">
                {step === WIZARD_STEPS.WELCOME && (
                    <div className="wizard-step">
                        <h3>Welcome to the {project.name} Setup Wizard</h3>
                        <p className="wizard-text">
                            This wizard will guide you through the "installation" of {project.name}{' '}
                            version {project.version}.
                        </p>
                        <div className="wizard-info-box">
                            <img
                                src={project.icon}
                                alt={project.name}
                                className="wizard-project-icon"
                            />
                            <div>
                                <strong>{project.name}</strong>
                                <p>{project.description}</p>
                            </div>
                        </div>
                        <p className="wizard-warning">
                            WARNING: This is strictly a portfolio demonstration. No actual software
                            will be installed on your computer.
                        </p>
                        <p>Click Next to continue.</p>
                    </div>
                )}

                {step === WIZARD_STEPS.LICENSE && (
                    <div className="wizard-step">
                        <h3>System Requirements</h3>
                        <p>
                            The following technologies are required and "used" by this application:
                        </p>
                        <div className="wizard-license-box">
                            {project.tech.map((t, i) => (
                                <div key={i} className="wizard-req-item">
                                    ‚úÖ {t}
                                </div>
                            ))}
                        </div>
                        <p>
                            This project is hosted on GitHub. Clicking "Next" will simulate the
                            installation process.
                        </p>
                    </div>
                )}

                {step === WIZARD_STEPS.INSTALLING && (
                    <div className="wizard-step">
                        <h3>Installing {project.name}...</h3>
                        <p className="wizard-status">
                            Please wait while Setup installs the application.
                        </p>

                        <div className="wizard-progress-track">
                            {/* Chunks style progress bar */}
                            {Array.from({ length: 20 }).map((_, i) => (
                                <div
                                    key={i}
                                    className={`wizard-progress-chunk ${i < Math.floor(progress / 5) ? 'filled' : ''}`}
                                ></div>
                            ))}
                        </div>

                        <div className="wizard-logs">
                            {installLog.map((log, i) => (
                                <div key={i}>{log}</div>
                            ))}
                        </div>
                    </div>
                )}

                {step === WIZARD_STEPS.FINISHED && (
                    <div className="wizard-step">
                        <h3>Installation Complete</h3>
                        <p>Setup has finished "installing" {project.name} on your computer.</p>
                        <p>The application may be launched by selecting the installed icons.</p>
                        <div className="wizard-final-option">
                            <input type="checkbox" checked readOnly />
                            <label>Launch {project.name} (Opens GitHub)</label>
                        </div>
                        <p>Click Finish to exit Setup.</p>
                    </div>
                )}
            </div>

            <div className="wizard-footer">
                <div className="wizard-footer-line"></div>
                <div className="wizard-buttons">
                    <button
                        className="wizard-btn"
                        disabled={
                            step === WIZARD_STEPS.WELCOME ||
                            step === WIZARD_STEPS.INSTALLING ||
                            step === WIZARD_STEPS.FINISHED
                        }
                        onClick={handleBack}
                    >
                        &lt; Back
                    </button>
                    <button
                        className="wizard-btn"
                        disabled={step === WIZARD_STEPS.INSTALLING}
                        onClick={handleNext}
                    >
                        {step === WIZARD_STEPS.FINISHED ? 'Finish' : 'Next >'}
                    </button>
                    <button className="wizard-btn" onClick={onClose}>
                        Cancel
                    </button>
                </div>
            </div>
        </div>
    )
}

export default ProjectViewer

import { useRef, useEffect } from 'react'
import { useRecentPrograms } from '../../context/RecentProgramsContext'
import styles from './StartMenu.module.css'

// Program labels for display
const PROGRAM_LABELS = {
    about: 'About Me',
    projects: 'Projects',
    contact: 'Contact',
    mycomputer: 'My Computer',
    resume: 'Resume',
    settings: 'Settings'
}

function StartMenu({ isOpen, onItemClick }) {
    const { recentPrograms } = useRecentPrograms()
    const menuRef = useRef(null)

    // Focus management
    useEffect(() => {
        if (isOpen && menuRef.current) {
            // Find first button to focus? 
            // Better to let user tab in or programmatically focus first item
            // preventing focus trap for now, just aria-hidden toggle
        }
    }, [isOpen])

    const handleClick = (e) => {
        e.stopPropagation()
        // Determine if click was on a button
        const button = e.target.closest('button')
        if (!button) return

        const openId = button.dataset.open
        const action = button.dataset.action

        if (action || openId) {
            onItemClick(action, openId)
        }
    }

    return (
        <div
            id="start-menu"
            ref={menuRef}
            className={`${styles.menu} ${isOpen ? styles.open : ''}`}
            aria-hidden={!isOpen}
            role="menu"
            onClick={handleClick}
        >
            <div className={styles.banner}>Borges OS</div>
            <div className={styles.items} role="group" aria-label="Programs">
                {/* Recently used programs (dynamic) */}
                {recentPrograms.length > 0 ? (
                    <>
                        <div className={styles.sectionLabel}>Recent</div>
                        {recentPrograms.map(programId => (
                            <button
                                key={programId}
                                className={styles.item}
                                data-open={programId}
                                role="menuitem"
                            >
                                {PROGRAM_LABELS[programId] || programId}
                            </button>
                        ))}
                        <div className={styles.separator}></div>
                    </>
                ) : (
                    <>
                        <button className={styles.item} data-open="about" role="menuitem">About Me</button>
                        <button className={styles.item} data-open="resume" role="menuitem">Resume</button>
                        <button className={styles.item} data-open="projects" role="menuitem">Projects</button>
                        <button className={styles.item} data-open="contact" role="menuitem">Contact</button>
                        <div className={styles.separator}></div>
                    </>
                )}

                {/* Fixed items */}
                <div className={styles.sectionLabel}>Programs</div>
                <button className={styles.item} data-open="fileexplorer" role="menuitem">üìÅ File Explorer</button>
                <button className={styles.item} data-open="terminal" role="menuitem">üíª Command Prompt</button>
                <button className={styles.item} data-open="notepad" role="menuitem">üìù Notepad</button>
                <button className={styles.item} data-open="paint" role="menuitem">üé® Paint</button>
                <button className={styles.item} data-open="minesweeper" role="menuitem">üí£ Minesweeper</button>
                <button className={styles.item} data-open="outlook" role="menuitem">üìß Outlook Express</button>
                <div className={styles.separator}></div>
                <button className={styles.item} data-open="settings" role="menuitem">‚öôÔ∏è Settings</button>
                <button className={styles.item} data-action="run" role="menuitem">‚ñ∂Ô∏è Run...</button>
                <button className={styles.item} data-action="shutdown" role="menuitem">üî¥ Shut Down...</button>
            </div>
        </div>
    )
}

export default StartMenu

