import { useState, useEffect, useCallback, useRef } from 'react'
import Desktop from './components/Desktop'
import Taskbar from './components/Taskbar'
import Window from './components/Window'
import StartMenu from './components/StartMenu'
import BiosScreen from './components/BiosScreen'
import FileExplorer from './components/FileExplorer'
import LoginScreen from './components/LoginScreen'
import ErrorDialog from './components/ErrorDialog'
import Terminal from './components/Terminal'
import ProjectViewer from './components/ProjectViewer'
import Minesweeper from './components/Minesweeper'
import BSOD from './components/BSOD'
import MobilePortfolio from './components/MobilePortfolio'

import { useSounds } from './hooks/useSounds'
import { useWindowManager } from './hooks/useWindowManager'
import { WINDOW_CONFIGS } from './config/windows'
import { ICONS } from './config/icons'

// System states
const SYSTEM_STATE = {
    WAITING: 'waiting', // Waiting for user to focus window
    BOOTING: 'booting', // BIOS boot sequence
    LOGGING: 'logging', // Login screen with startup sound
    RUNNING: 'running', // Desktop is active
    SHUTDOWN: 'shutdown' // Shutdown sequence
}

function App() {
    const { playError } = useSounds()
    const {
        windowStates,
        openWindows,
        activeWindowId,
        openWindow,
        closeWindow,
        minimizeWindow,
        toggleMaximize,
        bringToFront,
        handleDragStart,
        handleResizeStart,
        handleContentResize
    } = useWindowManager()

    const [runErrorDialog, setRunErrorDialog] = useState(null)
    const [startMenuOpen, setStartMenuOpen] = useState(false)
    const [runInput, setRunInput] = useState('')
    const [showBSOD, setShowBSOD] = useState(false)

    // Mobile detection
    const [isMobile, setIsMobile] = useState(() => {
        if (typeof window === 'undefined') return false
        return window.innerWidth <= 768 || ('ontouchstart' in window && window.innerWidth <= 1024)
    })

    // Listen for resize events to update mobile detection
    useEffect(() => {
        const checkMobile = () => {
            setIsMobile(
                window.innerWidth <= 768 || ('ontouchstart' in window && window.innerWidth <= 1024)
            )
        }
        window.addEventListener('resize', checkMobile)
        return () => window.removeEventListener('resize', checkMobile)
    }, [])

    // System boot state
    const [systemState, setSystemState] = useState(() => {
        // Check if we've already booted this session
        const hasBooted = sessionStorage.getItem('borgesOS-booted')
        if (hasBooted) return SYSTEM_STATE.RUNNING

        // If window is already focused, start booting immediately
        if (typeof document !== 'undefined' && document.hasFocus()) {
            return SYSTEM_STATE.BOOTING
        }
        return SYSTEM_STATE.WAITING
    })

    // Handle window focus to start boot sequence
    useEffect(() => {
        if (systemState !== SYSTEM_STATE.WAITING) return

        const handleFocus = () => {
            setSystemState(SYSTEM_STATE.BOOTING)
        }

        window.addEventListener('focus', handleFocus)
        return () => window.removeEventListener('focus', handleFocus)
    }, [systemState])

    // Handle boot completion
    const handleBootComplete = useCallback(() => {
        sessionStorage.setItem('borgesOS-booted', 'true')
        setSystemState(SYSTEM_STATE.RUNNING)
    }, [])

    // Handle shutdown completion
    const handleShutdownComplete = useCallback(() => {
        // Clear session and reload
        sessionStorage.removeItem('borgesOS-booted')
        sessionStorage.removeItem('borgesOS-resume-opened')
        localStorage.removeItem('win95-recent-programs')
        window.location.reload()
    }, [])

    // Auto-open Resume after boot completes
    const hasOpenedResumeRef = useRef(false)
    useEffect(() => {
        if (systemState === SYSTEM_STATE.RUNNING && !hasOpenedResumeRef.current) {
            // Check if this is a fresh boot (not page refresh during same session)
            const isFirstBoot = !sessionStorage.getItem('borgesOS-resume-opened')
            if (isFirstBoot) {
                // Small delay for smooth transition from BIOS to desktop
                const timer = setTimeout(() => {
                    openWindow('resume')
                    sessionStorage.setItem('borgesOS-resume-opened', 'true')
                }, 300)
                hasOpenedResumeRef.current = true
                return () => clearTimeout(timer)
            }
        }
    }, [systemState, openWindow])

    // Taskbar click handler
    const handleTaskbarClick = useCallback(
        (winId) => {
            const state = windowStates[winId]
            if (!state.isOpen || state.isMinimized) {
                openWindow(winId)
            } else if (activeWindowId === winId) {
                minimizeWindow(winId)
            } else {
                bringToFront(winId)
            }
        },
        [windowStates, activeWindowId, openWindow, minimizeWindow, bringToFront]
    )

    // Start menu handlers
    const toggleStartMenu = () => setStartMenuOpen((prev) => !prev)
    const closeStartMenu = useCallback(() => setStartMenuOpen(false), [])

    // Run dialog handlers
    const handleRunSubmit = (e) => {
        e.preventDefault()
        const cmd = runInput.trim().toLowerCase()
        if (!cmd) return

        const aliases = ['about', 'projects', 'contact', 'mycomputer', 'resume', 'run', 'settings']
        if (aliases.includes(cmd)) {
            openWindow(cmd)
            closeWindow('run')
            setRunInput('')
        } else if (cmd.startsWith('http://') || cmd.startsWith('https://')) {
            window.open(cmd, '_blank')
            closeWindow('run')
            setRunInput('')
        } else {
            playError()
            setRunErrorDialog({
                title: 'Run',
                message: `Cannot find file '${cmd}' (or one of its components). Make sure the path and filename are correct and that all required libraries are available.`,
                type: 'error'
            })
        }
    }

    // Start menu item click
    const handleStartItemClick = (action, openId) => {
        if (openId) openWindow(openId)
        if (action === 'run') openWindow('run')
        if (action === 'shutdown') {
            // Start shutdown sequence
            closeStartMenu()
            setSystemState(SYSTEM_STATE.SHUTDOWN)
            return
        }
        closeStartMenu()
    }

    // Close start menu on outside click
    useEffect(() => {
        const handleClick = () => closeStartMenu()
        document.addEventListener('click', handleClick)
        return () => document.removeEventListener('click', handleClick)
    }, [])

    // Keyboard shortcuts
    useEffect(() => {
        const handleKeyDown = (e) => {
            // Don't trigger shortcuts when typing in input fields
            if (e.target.tagName === 'INPUT' || e.target.tagName === 'TEXTAREA') {
                // Only allow Escape in input fields
                if (e.key === 'Escape') {
                    if (windowStates.run?.isOpen && !windowStates.run?.isMinimized) {
                        closeWindow('run')
                    }
                }
                return
            }

            // Ctrl + R: Open Run dialog
            if (e.ctrlKey && e.key.toLowerCase() === 'r') {
                e.preventDefault()
                openWindow('run')
                return
            }

            // Alt + F4: Close active window
            if (e.altKey && e.key === 'F4') {
                e.preventDefault()
                if (activeWindowId) {
                    closeWindow(activeWindowId)
                }
                return
            }

            // Escape: Close run window, start menu, or deselect active window
            if (e.key === 'Escape') {
                if (startMenuOpen) {
                    closeStartMenu()
                } else if (windowStates.run?.isOpen && !windowStates.run?.isMinimized) {
                    closeWindow('run')
                    // Also clear potentially stuck active state
                    if (activeWindowId === 'run') minimizeWindow('run')
                } else if (activeWindowId) {
                    minimizeWindow(activeWindowId)
                }
                return
            }

            // Ctrl + M: Minimize active window
            if (e.ctrlKey && e.key.toLowerCase() === 'm') {
                e.preventDefault()
                if (activeWindowId) {
                    minimizeWindow(activeWindowId)
                }
                return
            }

            // Ctrl + 1-6: Quick open windows
            if (e.ctrlKey && e.key >= '1' && e.key <= '6') {
                e.preventDefault()
                const windowMap = {
                    1: 'about',
                    2: 'projects',
                    3: 'contact',
                    4: 'mycomputer',
                    5: 'resume',
                    6: 'settings'
                }
                const windowId = windowMap[e.key]
                if (windowId) {
                    openWindow(windowId)
                }
                return
            }
        }

        document.addEventListener('keydown', handleKeyDown)
        return () => document.removeEventListener('keydown', handleKeyDown)
    }, [
        windowStates.run,
        activeWindowId,
        startMenuOpen,
        closeWindow,
        openWindow,
        minimizeWindow,
        closeStartMenu
    ])

    // Show mobile portfolio on mobile devices
    if (isMobile) {
        return <MobilePortfolio />
    }

    // Show BIOS screen during boot or shutdown
    if (systemState === SYSTEM_STATE.WAITING) {
        return (
            <div className="bios-screen waiting-screen">
                <div className="bios-content">
                    <div className="bios-line">Click anywhere or focus window to start...</div>
                    <span className="bios-cursor visible">█</span>
                </div>
            </div>
        )
    }

    if (systemState === SYSTEM_STATE.BOOTING) {
        return <BiosScreen mode="boot" onComplete={() => setSystemState(SYSTEM_STATE.LOGGING)} />
    }

    if (systemState === SYSTEM_STATE.LOGGING) {
        return <LoginScreen onComplete={handleBootComplete} />
    }

    if (systemState === SYSTEM_STATE.SHUTDOWN) {
        return <BiosScreen mode="shutdown" onComplete={handleShutdownComplete} />
    }

    return (
        <>
            <Desktop
                icons={ICONS.filter((icon) => icon.showOnDesktop !== false)}
                onIconDoubleClick={openWindow}
                onContextMenuAction={(action) => {
                    if (action === 'properties') {
                        openWindow('settings')
                    }
                    if (action === 'system_properties') {
                        openWindow('system_properties')
                    }
                }}
            />

            {/* Regular windows */}
            {Object.entries(WINDOW_CONFIGS).map(([id, config]) => (
                <Window
                    key={id}
                    id={id}
                    title={config.title}
                    icon={config.icon}
                    state={windowStates[id]}
                    isActive={activeWindowId === id}
                    onClose={() => closeWindow(id)}
                    onMinimize={() => minimizeWindow(id)}
                    onMaximize={() => toggleMaximize(id)}
                    onTitlebarMouseDown={(e) => handleDragStart(e, id)}
                    onTitlebarDoubleClick={() => toggleMaximize(id)}
                    onWindowMouseDown={() => {
                        if (windowStates[id].isOpen && !windowStates[id].isMinimized) {
                            bringToFront(id)
                        }
                    }}
                    onResizeStart={
                        config.resizable !== false
                            ? (e, direction) => handleResizeStart(e, id, direction)
                            : undefined
                    }
                    bodyStyle={config.bodyStyle}
                >
                    {id === 'fileexplorer' ? (
                        <FileExplorer onOpenWindow={openWindow} />
                    ) : id === 'terminal' ? (
                        <Terminal openWindow={openWindow} triggerBSOD={() => setShowBSOD(true)} />
                    ) : id === 'minesweeper' ? (
                        <Minesweeper
                            onResize={(w, h) => handleContentResize(id, w, h)}
                            onClose={() => closeWindow(id)}
                        />
                    ) : id.startsWith('project_') ? (
                        <ProjectViewer
                            projectId={id.replace('project_', '')}
                            isOpen={windowStates[id]?.isOpen}
                            onOpenWindow={openWindow}
                            onClose={() => closeWindow(id)}
                        />
                    ) : (
                        config.content
                    )}
                </Window>
            ))}

            {/* Run window */}
            <Window
                id="run"
                title="Run"
                icon="icons/file_program_group-0.png"
                state={windowStates.run}
                hideMaximize={true}
                isActive={activeWindowId === 'run'}
                onClose={() => closeWindow('run')}
                onMinimize={() => minimizeWindow('run')}
                onMaximize={() => toggleMaximize('run')}
                onTitlebarMouseDown={(e) => handleDragStart(e, 'run')}
                onTitlebarDoubleClick={() => toggleMaximize('run')}
                onWindowMouseDown={() => {
                    if (windowStates.run?.isOpen && !windowStates.run?.isMinimized) {
                        bringToFront('run')
                    }
                }}
                onResizeStart={(e, direction) => handleResizeStart(e, 'run', direction)}
                className="run-window"
                bodyStyle={{ padding: '14px' }}
            >
                <form onSubmit={handleRunSubmit} className="run-form">
                    <div className="run-top-section">
                        <img
                            src="icons/file_program_group-0.png"
                            alt=""
                            className="run-dialog-icon"
                        />
                        <div className="run-description">
                            <p>
                                Type the name of a program, folder, document, or Internet resource,
                                and Windows will open it for you.
                            </p>
                        </div>
                    </div>

                    <div className="run-input-section">
                        <label>Open:</label>
                        <div className="run-combobox">
                            <input
                                type="text"
                                value={runInput}
                                onChange={(e) => setRunInput(e.target.value)}
                                autoFocus
                                className="combobox-input"
                            />
                            <div className="combobox-button">
                                <span>▼</span>
                            </div>
                        </div>
                    </div>

                    <div className="run-buttons">
                        <button type="submit" className="run-btn">
                            OK
                        </button>
                        <button
                            type="button"
                            className="run-btn"
                            onClick={() => closeWindow('run')}
                        >
                            Cancel
                        </button>
                    </div>
                </form>
            </Window>

            {/* BSOD */}
            {showBSOD && <BSOD onRestart={handleShutdownComplete} />}

            {/* Error Dialog */}
            {runErrorDialog && (
                <ErrorDialog
                    title={runErrorDialog.title}
                    message={runErrorDialog.message}
                    type={runErrorDialog.type}
                    onClose={() => setRunErrorDialog(null)}
                />
            )}

            {/* Taskbar */}
            <Taskbar
                openWindows={openWindows}
                activeWindowId={activeWindowId}
                onTaskClick={handleTaskbarClick}
                onStartClick={(e) => {
                    e.stopPropagation()
                    toggleStartMenu()
                }}
            />

            {/* Start Menu */}
            <StartMenu isOpen={startMenuOpen} onItemClick={handleStartItemClick} />
        </>
    )
}

export default App

import { useState, useEffect } from 'react'
import LetterGlitch from './LetterGlitch'
import { SYSTEM_INFO } from '../config/system'


// BIOS text lines to display progressively
const BOOT_SEQUENCE = [
    { text: 'BorgesOS BIOS v1.0', delay: 0 },
    { text: 'Copyright (C) 2026 Shane Borges', delay: 200 },
    { text: '', delay: 300 },
    { text: 'Initializing system...', delay: 400 },
    { text: 'Detecting hardware...', delay: 700 },
    { text: `  CPU: ${SYSTEM_INFO.cpu} ... OK`, delay: 900 },
    { text: `  RAM: ${SYSTEM_INFO.ram} ... OK`, delay: 1100 },
    { text: `  HDD: ${SYSTEM_INFO.hdd} ... OK`, delay: 1300 },
    { text: `  GPU: ${SYSTEM_INFO.gpu} ... OK`, delay: 1500 },
    { text: '', delay: 1600 },
    { text: 'Loading BorgesOS...', delay: 1800 },
    { text: '', delay: 2200 },
    { text: 'Press any key to continue...', delay: 2500 }
]

const SHUTDOWN_SEQUENCE = [
    { text: 'Shutting down BorgesOS...', delay: 0 },
    { text: '', delay: 200 },
    { text: 'Saving user settings...', delay: 400 },
    { text: 'Closing applications...', delay: 700 },
    { text: 'Unmounting drives...', delay: 1000 },
    { text: '', delay: 1200 },
    { text: 'It is now safe to restart your computer.', delay: 1400 },
    { text: '', delay: 1600 },
    { text: 'Press any key to restart...', delay: 1800 }
]

function BiosScreen({ mode, onComplete }) {
    const [visibleLines, setVisibleLines] = useState([])
    const [showCursor, setShowCursor] = useState(true)
    const [waitingForInput, setWaitingForInput] = useState(false)

    const sequence = mode === 'boot' ? BOOT_SEQUENCE : SHUTDOWN_SEQUENCE
    const lastMessageDelay = sequence[sequence.length - 1].delay

    useEffect(() => {
        // Show lines progressively
        const timers = sequence.map((line, index) => {
            return setTimeout(() => {
                setVisibleLines((prev) => [...prev, line.text])
            }, line.delay)
        })

        // After last message, wait for user input
        const waitTimer = setTimeout(() => {
            setWaitingForInput(true)
        }, lastMessageDelay + 200)

        // Cursor blink
        const cursorInterval = setInterval(() => {
            setShowCursor((prev) => !prev)
        }, 500)

        return () => {
            timers.forEach((t) => clearTimeout(t))
            clearTimeout(waitTimer)
            clearInterval(cursorInterval)
        }
    }, [mode, lastMessageDelay])

    // Listen for any key or click to continue
    useEffect(() => {
        if (!waitingForInput) return

        const handleInput = () => {
            if (onComplete) onComplete()
        }

        document.addEventListener('keydown', handleInput)
        document.addEventListener('click', handleInput)

        return () => {
            document.removeEventListener('keydown', handleInput)
            document.removeEventListener('click', handleInput)
        }
    }, [waitingForInput, onComplete])

    return (
        <div className="bios-screen">
            <LetterGlitch
                glitchColors={['#00ff00', '#00cc00', '#006600']}
                glitchSpeed={40}
                outerVignette={true}
                centerVignette={false}
                smooth={true}
                opacity={0.15}
            />
            <div className="bios-content">
                {visibleLines.map((line, i) => (
                    <div key={i} className="bios-line">
                        {line || '\u00A0'}
                    </div>
                ))}
                <span className={`bios-cursor ${showCursor ? 'visible' : ''}`}>█</span>
            </div>
        </div>
    )
}

export default BiosScreen

import { useState, useRef, useEffect, useCallback } from 'react'
import { ICONS } from '../config/icons'

// Generate Desktop children from ICONS array (only those shown on desktop)
const generateDesktopChildren = () => {
    const children = {}
    ICONS.filter((icon) => icon.showOnDesktop !== false).forEach((icon) => {
        children[icon.label] = {
            type:
                icon.id === 'fileexplorer' || icon.id === 'mycomputer' || icon.id === 'settings'
                    ? 'shortcut'
                    : 'file',
            name: icon.label,
            icon: icon.icon,
            windowId: icon.id
        }
    })
    return children
}

// File system structure mimicking Windows 95
const FILE_SYSTEM = {
    'C:': {
        type: 'drive',
        name: 'Local Disk (C:)',
        icon: 'icons/hard_disk_drive-0.png',
        children: {
            Desktop: {
                type: 'folder',
                name: 'Desktop',
                icon: 'icons/directory_closed_cool-0.png',
                children: generateDesktopChildren()
            },
            Documents: {
                type: 'folder',
                name: 'My Documents',
                icon: 'icons/directory_closed_cool-0.png',
                children: {
                    'Resume.pdf': {
                        type: 'file',
                        name: 'Resume',
                        icon: 'icons/write_file-0.png',
                        windowId: 'resume'
                    }
                }
            },
            Projects: {
                type: 'folder',
                name: 'Projects',
                icon: 'icons/directory_closed_cool-0.png',
                children: {
                    Python: {
                        type: 'folder',
                        name: 'Python',
                        icon: 'icons/directory_closed_cool-0.png',
                        children: {
                            DiceGame: {
                                type: 'project',
                                name: 'Dice Game',
                                id: 'dice-game',
                                icon: 'icons/game_solitaire-0.png'
                            },
                            CarRacer: {
                                type: 'project',
                                name: 'Car Racer',
                                id: 'car-racer',
                                icon: 'icons/joystick-0.png'
                            },
                            PasswordMaker: {
                                type: 'project',
                                name: 'Password Maker',
                                id: 'password-maker',
                                icon: 'icons/key_win-0.png'
                            },
                            CommonFactors: {
                                type: 'project',
                                name: 'Common Factors',
                                id: 'common-factors',
                                icon: 'icons/calculator-0.png'
                            }
                        }
                    },
                    Assembly: {
                        type: 'folder',
                        name: 'Assembly',
                        icon: 'icons/directory_closed_cool-0.png',
                        children: {
                            Calculator: {
                                type: 'project',
                                name: 'Assembly Calculator',
                                id: 'assembly-calculator',
                                icon: 'icons/processor-0.png'
                            }
                        }
                    }
                }
            }
        }
    }
}

function TreeItem({
    item,
    path,
    level = 0,
    selectedPath,
    onSelect,
    expandedPaths,
    onToggle,
    onDoubleClick
}) {

    const isFolder = item.type === 'folder' || item.type === 'drive'
    const fullPath = path
    const isExpanded = expandedPaths.includes(fullPath)
    const isSelected = selectedPath === fullPath

    const handleClick = () => {
        if (isFolder) {
            onToggle(fullPath)
        }
        onSelect(fullPath, item)
    }

    const handleDoubleClick = () => {
        if (onDoubleClick) {
            onDoubleClick(fullPath, item)
        }
    }

    return (
        <div>
            <div
                className={`tree-item ${isSelected ? 'selected' : ''}`}
                style={{ paddingLeft: `${8 + level * 16}px` }}
                onClick={handleClick}
                onDoubleClick={handleDoubleClick}
            >
                {isFolder && <span className="tree-item-toggle">{isExpanded ? '▼' : '▶'}</span>}
                <img
                    src={
                        isFolder && isExpanded && item.icon === 'icons/directory_closed_cool-0.png'
                            ? 'icons/directory_open_cool-0.png'
                            : item.icon || 'icons/directory_closed_cool-0.png'
                    }
                    alt=""
                    className="tree-item-icon"
                />
                <span style={{ marginLeft: 4 }}>{item.name}</span>
            </div>
            {isFolder && isExpanded && item.children && (
                <div className="tree-children">
                    {Object.entries(item.children).map(([key, child]) => (
                        <TreeItem
                            key={key}
                            item={child}
                            path={`${fullPath}\\${key}`}
                            level={level + 1}
                            selectedPath={selectedPath}
                            onSelect={onSelect}
                            expandedPaths={expandedPaths}
                            onToggle={onToggle}
                            onDoubleClick={onDoubleClick}
                        />
                    ))}
                </div>
            )}
        </div>
    )
}

function FileItem({ item, itemKey, onDoubleClick }) {


    return (
        <div className="file-item" onDoubleClick={() => onDoubleClick(item, itemKey)}>
            <img
                src={item.icon || 'icons/directory_closed_cool-0.png'}
                alt=""
                className="file-item-icon"
            />
            <span className="file-item-name">{item.name}</span>
        </div>
    )
}

function FileExplorer({ onOpenWindow }) {
    const [currentPath, setCurrentPath] = useState('C:\\Desktop')
    const [selectedPath, setSelectedPath] = useState('C:\\Desktop')
    const [expandedPaths, setExpandedPaths] = useState([
        'C:',
        'C:\\Projects',
        'C:\\Projects\\Python'
    ])
    const [history, setHistory] = useState(['C:\\Desktop'])
    const [historyIndex, setHistoryIndex] = useState(0)
    const [addressInput, setAddressInput] = useState('C:\\Desktop')
    const [sidebarWidth, setSidebarWidth] = useState(180)

    const sidebarRef = useRef(null)
    const isResizing = useRef(false)

    // Sidebar resize handler
    const handleResizeMouseDown = useCallback((e) => {
        e.preventDefault()
        isResizing.current = true
        document.body.style.cursor = 'ew-resize'
        document.body.style.userSelect = 'none'
    }, [])

    useEffect(() => {
        const handleMouseMove = (e) => {
            if (!isResizing.current || !sidebarRef.current) return
            const sidebarRect = sidebarRef.current.getBoundingClientRect()
            const newWidth = e.clientX - sidebarRect.left
            if (newWidth >= 100 && newWidth <= 400) {
                setSidebarWidth(newWidth)
            }
        }

        const handleMouseUp = () => {
            isResizing.current = false
            document.body.style.cursor = ''
            document.body.style.userSelect = ''
        }

        document.addEventListener('mousemove', handleMouseMove)
        document.addEventListener('mouseup', handleMouseUp)
        return () => {
            document.removeEventListener('mousemove', handleMouseMove)
            document.removeEventListener('mouseup', handleMouseUp)
        }
    }, [])

    const toggleExpand = (path) => {
        setExpandedPaths((prev) =>
            prev.includes(path) ? prev.filter((p) => p !== path) : [...prev, path]
        )
    }

    // Get item at a specific path (moved up for use in navigation)
    const getItemAtPath = (path) => {
        const parts = path.split('\\').filter((p) => p)
        let current = FILE_SYSTEM[parts[0]]
        for (let i = 1; i < parts.length && current; i++) {
            current = current.children?.[parts[i]]
        }
        return current
    }

    const navigateTo = (path, item) => {
        if (item?.type === 'folder' || item?.type === 'drive') {
            setCurrentPath(path)
            setAddressInput(path)
            // Add to history
            const newHistory = history.slice(0, historyIndex + 1)
            newHistory.push(path)
            setHistory(newHistory)
            setHistoryIndex(newHistory.length - 1)
        }
        setSelectedPath(path)
    }

    // Handle address bar Enter key
    const handleAddressKeyDown = (e) => {
        if (e.key === 'Enter') {
            const item = getItemAtPath(addressInput)
            if (item && (item.type === 'folder' || item.type === 'drive')) {
                navigateTo(addressInput, item)
                // Expand path in tree
                const parts = addressInput.split('\\')
                const pathsToExpand = []
                for (let i = 1; i <= parts.length; i++) {
                    pathsToExpand.push(parts.slice(0, i).join('\\'))
                }
                setExpandedPaths((prev) => [...new Set([...prev, ...pathsToExpand])])
            }
        }
    }

    // Handle tree item double-click
    const handleTreeDoubleClick = (path, item) => {
        if (item.type === 'folder' || item.type === 'drive') {
            navigateTo(path, item)
        } else if (item.type === 'project' && item.id && onOpenWindow) {
            // Open project in project viewer window
            onOpenWindow(`project_${item.id.replace(/-/g, '')}`)
        } else if (item.windowId && onOpenWindow) {
            onOpenWindow(item.windowId)
        }
    }

    const goBack = () => {
        if (historyIndex > 0) {
            setHistoryIndex(historyIndex - 1)
            setCurrentPath(history[historyIndex - 1])
            setSelectedPath(history[historyIndex - 1])
        }
    }

    const goForward = () => {
        if (historyIndex < history.length - 1) {
            setHistoryIndex(historyIndex + 1)
            setCurrentPath(history[historyIndex + 1])
            setSelectedPath(history[historyIndex + 1])
        }
    }

    const goUp = () => {
        const parts = currentPath.split('\\')
        if (parts.length > 1) {
            const parentPath = parts.slice(0, -1).join('\\') || 'C:'
            const parentItem = getItemAtPath(parentPath)
            navigateTo(parentPath, parentItem)
        }
    }

    const currentItem = getItemAtPath(currentPath)
    const currentChildren = currentItem?.children || {}

    const handleItemDoubleClick = (item, key) => {
        const newPath = `${currentPath}\\${key}`

        if (item.type === 'folder') {
            navigateTo(newPath, item)
            if (!expandedPaths.includes(newPath)) {
                setExpandedPaths([...expandedPaths, newPath])
            }
        } else if (item.windowId && onOpenWindow) {
            onOpenWindow(item.windowId)
        } else if (item.type === 'project' && onOpenWindow) {
            // Open project wizard window - normalize ID (remove hyphens)
            const normalizedId = item.id.replace(/-/g, '')
            onOpenWindow('project_' + normalizedId)
        }
    }

    // Check if selected item is a project

    return (
        <div className="file-explorer">
            <div className="file-explorer-toolbar">
                <button
                    className="file-explorer-toolbar-btn"
                    onClick={goBack}
                    disabled={historyIndex === 0}
                    title="Back"
                >
                    ←
                </button>
                <button
                    className="file-explorer-toolbar-btn"
                    onClick={goForward}
                    disabled={historyIndex >= history.length - 1}
                    title="Forward"
                >
                    →
                </button>
                <button
                    className="file-explorer-toolbar-btn"
                    onClick={goUp}
                    disabled={currentPath === 'C:'}
                    title="Up One Level"
                >
                    ↑
                </button>
            </div>
            <div className="file-explorer-address">
                <span>Address:</span>
                <input
                    type="text"
                    className="file-explorer-address-input"
                    value={addressInput}
                    onChange={(e) => setAddressInput(e.target.value)}
                    onKeyDown={handleAddressKeyDown}
                    onBlur={() => setAddressInput(currentPath)}
                />
                <button
                    className="file-explorer-toolbar-btn"
                    onClick={() => {
                        const item = getItemAtPath(addressInput)
                        if (item && (item.type === 'folder' || item.type === 'drive')) {
                            navigateTo(addressInput, item)
                        }
                    }}
                    title="Go"
                >
                    →
                </button>
            </div>
            <div className="file-explorer-main">
                <div
                    className="file-explorer-sidebar"
                    ref={sidebarRef}
                    style={{ width: sidebarWidth }}
                >
                    {Object.entries(FILE_SYSTEM).map(([key, item]) => (
                        <TreeItem
                            key={key}
                            item={item}
                            path={key}
                            selectedPath={selectedPath}
                            onSelect={navigateTo}
                            expandedPaths={expandedPaths}
                            onToggle={toggleExpand}
                            onDoubleClick={handleTreeDoubleClick}
                        />
                    ))}
                    <div
                        className="file-explorer-resize-handle"
                        onMouseDown={handleResizeMouseDown}
                    />
                </div>
                <div className="file-explorer-content">
                    <div className="file-grid">
                        {Object.entries(currentChildren).map(([key, item]) => (
                            <FileItem
                                key={key}
                                item={item}
                                itemKey={key}
                                onDoubleClick={handleItemDoubleClick}
                            />
                        ))}
                        {Object.keys(currentChildren).length === 0 && (
                            <div style={{ padding: 20, color: '#808080' }}>
                                This folder is empty.
                            </div>
                        )}
                    </div>
                </div>
            </div>
            <div className="file-explorer-status">
                {Object.keys(currentChildren).length} object(s)
            </div>
        </div>
    )
}

export default FileExplorer

import { useRef, useEffect, useCallback } from 'react'

const LetterGlitch = ({
    glitchColors = ['#00ff00', '#00cc00', '#009900'], // Matrix-green colors for BIOS
    className = '',
    glitchSpeed = 50,
    centerVignette = false,
    outerVignette = true,
    smooth = true,
    characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789@#$%&*<>[]{}|/\\',
    opacity = 0.3
}) => {
    const canvasRef = useRef(null)
    const animationRef = useRef(null)
    const letters = useRef([])
    const grid = useRef({ columns: 0, rows: 0 })
    const context = useRef(null)
    const lastGlitchTime = useRef(Date.now())

    const lettersAndSymbols = Array.from(characters)

    const fontSize = 16
    const charWidth = 10
    const charHeight = 20

    const getRandomChar = useCallback(() => {
        return lettersAndSymbols[Math.floor(Math.random() * lettersAndSymbols.length)]
    }, [lettersAndSymbols])

    const getRandomColor = useCallback(() => {
        return glitchColors[Math.floor(Math.random() * glitchColors.length)]
    }, [glitchColors])

    const hexToRgb = (hex) => {
        const shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i
        hex = hex.replace(shorthandRegex, (m, r, g, b) => {
            return r + r + g + g + b + b
        })

        const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex)
        return result
            ? {
                r: parseInt(result[1], 16),
                g: parseInt(result[2], 16),
                b: parseInt(result[3], 16)
            }
            : null
    }

    const interpolateColor = (start, end, factor) => {
        const result = {
            r: Math.round(start.r + (end.r - start.r) * factor),
            g: Math.round(start.g + (end.g - start.g) * factor),
            b: Math.round(start.b + (end.b - start.b) * factor)
        }
        return `rgb(${result.r}, ${result.g}, ${result.b})`
    }

    const calculateGrid = (width, height) => {
        const columns = Math.ceil(width / charWidth)
        const rows = Math.ceil(height / charHeight)
        return { columns, rows }
    }

    const initializeLetters = useCallback((columns, rows) => {
        grid.current = { columns, rows }
        const totalLetters = columns * rows
        letters.current = Array.from({ length: totalLetters }, () => ({
            char: getRandomChar(),
            color: getRandomColor(),
            targetColor: getRandomColor(),
            colorProgress: 1
        }))
    }, [getRandomChar, getRandomColor])

    const drawLetters = useCallback(() => {
        if (!context.current || letters.current.length === 0) return
        const ctx = context.current
        const { width, height } = canvasRef.current.getBoundingClientRect()
        ctx.clearRect(0, 0, width, height)
        ctx.font = `${fontSize}px monospace`
        ctx.textBaseline = 'top'

        letters.current.forEach((letter, index) => {
            const x = (index % grid.current.columns) * charWidth
            const y = Math.floor(index / grid.current.columns) * charHeight
            ctx.fillStyle = letter.color
            ctx.fillText(letter.char, x, y)
        })
    }, [])

    const resizeCanvas = useCallback(() => {
        const canvas = canvasRef.current
        if (!canvas) return
        const parent = canvas.parentElement
        if (!parent) return

        const dpr = window.devicePixelRatio || 1
        const rect = parent.getBoundingClientRect()

        canvas.width = rect.width * dpr
        canvas.height = rect.height * dpr

        canvas.style.width = `${rect.width}px`
        canvas.style.height = `${rect.height}px`

        if (context.current) {
            context.current.setTransform(dpr, 0, 0, dpr, 0, 0)
        }

        const { columns, rows } = calculateGrid(rect.width, rect.height)
        initializeLetters(columns, rows)

        drawLetters()
    }, [initializeLetters, drawLetters])

    const updateLetters = useCallback(() => {
        if (!letters.current || letters.current.length === 0) return

        const updateCount = Math.max(1, Math.floor(letters.current.length * 0.05))

        for (let i = 0; i < updateCount; i++) {
            const index = Math.floor(Math.random() * letters.current.length)
            if (!letters.current[index]) continue

            letters.current[index].char = getRandomChar()
            letters.current[index].targetColor = getRandomColor()

            if (!smooth) {
                letters.current[index].color = letters.current[index].targetColor
                letters.current[index].colorProgress = 1
            } else {
                letters.current[index].colorProgress = 0
            }
        }
    }, [getRandomChar, getRandomColor, smooth])

    const handleSmoothTransitions = useCallback(() => {
        let needsRedraw = false
        letters.current.forEach((letter) => {
            if (letter.colorProgress < 1) {
                letter.colorProgress += 0.05
                if (letter.colorProgress > 1) letter.colorProgress = 1

                const startRgb = hexToRgb(letter.color)
                const endRgb = hexToRgb(letter.targetColor)
                if (startRgb && endRgb) {
                    letter.color = interpolateColor(startRgb, endRgb, letter.colorProgress)
                    needsRedraw = true
                }
            }
        })

        if (needsRedraw) {
            drawLetters()
        }
    }, [drawLetters])

    const animate = useCallback(() => {
        const now = Date.now()
        if (now - lastGlitchTime.current >= glitchSpeed) {
            updateLetters()
            drawLetters()
            lastGlitchTime.current = now
        }

        if (smooth) {
            handleSmoothTransitions()
        }

        animationRef.current = requestAnimationFrame(animate)
    }, [glitchSpeed, updateLetters, drawLetters, smooth, handleSmoothTransitions])

    useEffect(() => {
        const canvas = canvasRef.current
        if (!canvas) return

        context.current = canvas.getContext('2d')
        resizeCanvas()
        animate()

        let resizeTimeout

        const handleResize = () => {
            clearTimeout(resizeTimeout)
            resizeTimeout = setTimeout(() => {
                cancelAnimationFrame(animationRef.current)
                resizeCanvas()
                animate()
            }, 100)
        }

        window.addEventListener('resize', handleResize)

        return () => {
            cancelAnimationFrame(animationRef.current)
            window.removeEventListener('resize', handleResize)
        }
    }, [resizeCanvas, animate])

    const containerStyle = {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        backgroundColor: 'transparent',
        overflow: 'hidden',
        opacity: opacity,
        zIndex: 0
    }

    const canvasStyle = {
        display: 'block',
        width: '100%',
        height: '100%'
    }

    const outerVignetteStyle = {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        background: 'radial-gradient(circle, rgba(0,0,0,0) 60%, rgba(0,0,0,1) 100%)'
    }

    const centerVignetteStyle = {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        pointerEvents: 'none',
        background: 'radial-gradient(circle, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0) 60%)'
    }

    return (
        <div style={containerStyle} className={className}>
            <canvas ref={canvasRef} style={canvasStyle} />
            {outerVignette && <div style={outerVignetteStyle}></div>}
            {centerVignette && <div style={centerVignetteStyle}></div>}
        </div>
    )
}

export default LetterGlitch

import { useState, useEffect, useRef } from 'react'

// Get base path for sounds (dev vs prod)
const getBasePath = () => {
    if (window.location.pathname.includes('/Portfolio')) {
        return '/Portfolio'
    }
    return ''
}

function LoginScreen({ onComplete }) {
    const [phase, setPhase] = useState('welcome') // welcome, logging, complete
    const [progress, setProgress] = useState(0)
    const audioPlayedRef = useRef(false)

    // Play startup sound when component mounts
    useEffect(() => {
        if (!audioPlayedRef.current) {
            const basePath = getBasePath()
            const audio = new Audio(`${basePath}/sounds/startup.wav`)
            audio.volume = 0.5
            audio.play().catch(() => { })
            audioPlayedRef.current = true
        }
    }, [])

    // Progress animation - runs for about 4 seconds (100 steps at 40ms = 4s)
    useEffect(() => {
        if (phase === 'logging') {
            const interval = setInterval(() => {
                setProgress((prev) => {
                    if (prev >= 100) {
                        clearInterval(interval)
                        setPhase('complete')
                        return 100
                    }
                    return prev + 1
                })
            }, 40) // 100 steps * 40ms = 4 seconds
            return () => clearInterval(interval)
        }
    }, [phase])

    // Welcome phase - show for 1.5 seconds
    useEffect(() => {
        if (phase === 'welcome') {
            const timer = setTimeout(() => setPhase('logging'), 1500)
            return () => clearTimeout(timer)
        }
    }, [phase])

    // Complete phase - show for 0.5 seconds then transition
    // Total: 1.5s welcome + 4s logging + 0.5s complete = 6 seconds (matches startup.wav)
    useEffect(() => {
        if (phase === 'complete') {
            const timer = setTimeout(() => {
                if (onComplete) onComplete()
            }, 500)
            return () => clearTimeout(timer)
        }
    }, [phase, onComplete])

    return (
        <div className="login-screen">
            <div className="login-box">
                <div className="login-header">
                    <img src="icons/computer.svg" alt="" className="login-logo" />
                    <h2>BorgesOS</h2>
                </div>

                {phase === 'welcome' && (
                    <div className="login-content">
                        <p className="login-welcome">Welcome</p>
                    </div>
                )}

                {phase === 'logging' && (
                    <div className="login-content">
                        <p className="login-status">Logging on...</p>
                        <div className="login-progress">
                            <div className="login-progress-bar" style={{ width: `${progress}%` }} />
                        </div>
                    </div>
                )}

                {phase === 'complete' && (
                    <div className="login-content">
                        <p className="login-status">Loading personal settings...</p>
                    </div>
                )}
            </div>
        </div>
    )
}

export default LoginScreen

import { useState, useEffect, useCallback, useRef } from 'react'
import '../index.css'

const DIFFICULTIES = {
    beginner: { rows: 9, cols: 9, mines: 10 },
    intermediate: { rows: 16, cols: 16, mines: 40 },
    expert: { rows: 16, cols: 30, mines: 99 }
}

const CELL_STATE = {
    HIDDEN: 0,
    REVEALED: 1,
    FLAGGED: 2,
    QUESTION: 3
}

const GAME_STATE = {
    IDLE: 'idle',
    PLAYING: 'playing',
    WON: 'won',
    LOST: 'lost'
}

const Minesweeper = ({ onResize, onClose }) => {
    const [difficulty, setDifficulty] = useState('beginner')
    const [grid, setGrid] = useState([])
    const [gameState, setGameState] = useState(GAME_STATE.IDLE)
    const [minesLeft, setMinesLeft] = useState(0)
    const [time, setTime] = useState(0)
    const [isMouseDown, setIsMouseDown] = useState(false)
    const [activeMenu, setActiveMenu] = useState(null)

    const timerRef = useRef(null)
    const menuRef = useRef(null)

    // Close menu when clicking outside
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setActiveMenu(null)
            }
        }
        document.addEventListener('mousedown', handleClickOutside)
        return () => document.removeEventListener('mousedown', handleClickOutside)
    }, [])

    const updateWindowSize = useCallback(
        (rows, cols) => {
            if (!onResize) return

            // Exact measurements based on Windows 95
            // Content Heights:
            // Menu Bar: ~24px
            // Game Header: ~46px
            // Board Borders/Padding: ~12px
            // Total Content Overhead: ~82px
            //
            // Window Chrome Heights (from Window.jsx):
            // Titlebar: ~30px
            // Window Borders: ~8px (4px top + 4px bottom)
            // Total Chrome Overhead: ~38px
            //
            // Total Height Required = BoardHeight + ContentOverhead + ChromeOverhead
            // Height = (Rows * 16) + 82 + 38 = (Rows * 16) + 120
            // Adding slight buffer for safety -> 140px

            const boardWidth = cols * 16
            const boardHeight = rows * 16

            const width = boardWidth + 40 // Ensure enough horizontal space
            const height = boardHeight + 140

            onResize(width, height)
        },
        [onResize]
    )

    const initGame = useCallback(
        (diff = difficulty) => {
            const config = DIFFICULTIES[diff]
            const { rows, cols, mines } = config

            // If changing difficulty, trigger resize
            if (diff !== difficulty || !grid.length) {
                updateWindowSize(rows, cols)
            }

            // If we are strictly resetting, we might want to keep the same difficulty
            // but if passed a diff, update state.
            if (diff !== difficulty) {
                setDifficulty(diff)
            }

            const newGrid = Array(rows)
                .fill(null)
                .map(() =>
                    Array(cols)
                        .fill(null)
                        .map(() => ({
                            isMine: false,
                            state: CELL_STATE.HIDDEN,
                            neighborCount: 0
                        }))
                )

            setGrid(newGrid)
            setGameState(GAME_STATE.IDLE)
            setMinesLeft(mines)
            setTime(0)
            setActiveMenu(null)
            clearInterval(timerRef.current)
        },
        [difficulty, grid.length, updateWindowSize]
    )

    // Initial load
    useEffect(() => {
        if (grid.length === 0) {
            initGame('beginner')
        }
        return () => clearInterval(timerRef.current)
    }, [initGame, grid.length]) // Depend on grid.length to only run once if empty

    const placeMines = useCallback((firstRow, firstCol) => {
        const { rows, cols, mines } = DIFFICULTIES[difficulty]
        const newGrid = grid.map((row) => row.map((cell) => ({ ...cell })))

        let minesPlaced = 0
        while (minesPlaced < mines) {
            const r = Math.floor(Math.random() * rows)
            const c = Math.floor(Math.random() * cols)

            if (!newGrid[r][c].isMine && (r !== firstRow || c !== firstCol)) {
                newGrid[r][c].isMine = true
                minesPlaced++
            }
        }

        // Calculate neighbors
        for (let r = 0; r < rows; r++) {
            for (let c = 0; c < cols; c++) {
                if (!newGrid[r][c].isMine) {
                    let count = 0
                    for (let i = -1; i <= 1; i++) {
                        for (let j = -1; j <= 1; j++) {
                            const nr = r + i
                            const nc = c + j
                            if (
                                nr >= 0 &&
                                nr < rows &&
                                nc >= 0 &&
                                nc < cols &&
                                newGrid[nr][nc].isMine
                            ) {
                                count++
                            }
                        }
                    }
                    newGrid[r][c].neighborCount = count
                }
            }
        }
        return newGrid
    }, [difficulty, grid])

    const startGame = (r, c) => {
        const newGrid = placeMines(r, c)
        // No setGrid here, returned to caller
        setGameState(GAME_STATE.PLAYING)

        timerRef.current = setInterval(() => {
            setTime((prev) => Math.min(prev + 1, 999))
        }, 1000)

        return newGrid
    }

    const revealCell = (currentGrid, r, c) => {
        if (
            currentGrid[r][c].state !== CELL_STATE.HIDDEN &&
            currentGrid[r][c].state !== CELL_STATE.QUESTION
        )
            return

        currentGrid[r][c].state = CELL_STATE.REVEALED

        if (currentGrid[r][c].isMine) {
            return
        }

        if (currentGrid[r][c].neighborCount === 0) {
            const { rows, cols } = DIFFICULTIES[difficulty]
            for (let i = -1; i <= 1; i++) {
                for (let j = -1; j <= 1; j++) {
                    const nr = r + i
                    const nc = c + j
                    if (nr >= 0 && nr < rows && nc >= 0 && nc < cols) {
                        revealCell(currentGrid, nr, nc)
                    }
                }
            }
        }
    }

    const handleCellClick = (r, c) => {
        if (gameState === GAME_STATE.WON || gameState === GAME_STATE.LOST) return
        if (grid[r][c].state === CELL_STATE.FLAGGED) return

        let newGrid

        if (gameState === GAME_STATE.IDLE) {
            newGrid = startGame(r, c)
            revealCell(newGrid, r, c)
            setGrid(newGrid)
            return
        }

        // Playing state
        newGrid = grid.map((row) => row.map((cell) => ({ ...cell })))
        const cell = newGrid[r][c]

        if (cell.isMine) {
            cell.isExploded = true
            cell.state = CELL_STATE.REVEALED
            setGrid(newGrid)
            gameOver(newGrid)
        } else {
            revealCell(newGrid, r, c)
            setGrid(newGrid)
            checkWin(newGrid)
        }
    }

    const handleCellRightClick = (e, r, c) => {
        e.preventDefault()
        if (gameState !== GAME_STATE.PLAYING && gameState !== GAME_STATE.IDLE) return
        if (grid[r][c].state === CELL_STATE.REVEALED) return

        const newGrid = grid.map((row) => row.map((cell) => ({ ...cell })))
        const cell = newGrid[r][c]

        if (cell.state === CELL_STATE.HIDDEN) {
            cell.state = CELL_STATE.FLAGGED
            setMinesLeft((m) => m - 1)
        } else if (cell.state === CELL_STATE.FLAGGED) {
            cell.state = CELL_STATE.QUESTION
            setMinesLeft((m) => m + 1)
        } else if (cell.state === CELL_STATE.QUESTION) {
            cell.state = CELL_STATE.HIDDEN
        }
        setGrid(newGrid)
    }

    const gameOver = (finalGrid) => {
        setGameState(GAME_STATE.LOST)
        clearInterval(timerRef.current)
        // Reveal all mines
        const newGrid = finalGrid.map((row) =>
            row.map((cell) => {
                if (cell.isMine && cell.state !== CELL_STATE.FLAGGED)
                    return { ...cell, state: CELL_STATE.REVEALED }
                if (!cell.isMine && cell.state === CELL_STATE.FLAGGED)
                    return { ...cell, isMisflagged: true }
                return cell
            })
        )
        setGrid(newGrid)
    }

    const checkWin = (currentGrid) => {
        const { rows, cols, mines } = DIFFICULTIES[difficulty]
        let hidden = 0
        for (let r = 0; r < rows; r++) {
            for (let c = 0; c < cols; c++) {
                if (currentGrid[r][c].state !== CELL_STATE.REVEALED) hidden++
            }
        }
        if (hidden === mines) {
            setGameState(GAME_STATE.WON)
            clearInterval(timerRef.current)
            setMinesLeft(0)
            // Flag remaining
            const newGrid = currentGrid.map((row) =>
                row.map((cell) => {
                    if (cell.isMine) return { ...cell, state: CELL_STATE.FLAGGED }
                    return cell
                })
            )
            setGrid(newGrid)
        }
    }



    const formatNumber = (num) => {
        if (num < -99) return '-99'
        if (num > 999) return '999'
        return Math.floor(num).toString().padStart(3, '0')
    }

    const handleMenuClick = (menu) => {
        setActiveMenu(activeMenu === menu ? null : menu)
    }

    const handleDifficultySelect = (diff) => {
        initGame(diff)
        setActiveMenu(null)
    }

    // Styles for the Seven-Segment Display
    const Counter = ({ value }) => (
        <div
            style={{
                background: '#000',
                color: '#ff0000',
                fontFamily: 'monospace',
                fontSize: '22px',
