import { useState, useRef, useEffect, useCallback } from 'react'

const TOOLS = {
    PENCIL: 'pencil',
    BRUSH: 'brush',
    ERASER: 'eraser',
    LINE: 'line',
    RECTANGLE: 'rectangle',
    ELLIPSE: 'ellipse',
    FILL: 'fill',
    EYEDROPPER: 'eyedropper',
    TEXT: 'text'
}

const COLOR_PALETTE = [
    '#000000',
    '#808080',
    '#800000',
    '#808000',
    '#008000',
    '#008080',
    '#000080',
