import { createContext, useContext, useState, useEffect, useCallback } from 'react'

const RecentProgramsContext = createContext(null)

const MAX_RECENT = 5

// Programs that should appear in recent list (excludes system functions like run, settings)
const TRACKABLE_PROGRAMS = ['about', 'projects', 'contact', 'mycomputer', 'resume']

export function RecentProgramsProvider({ children }) {
    const [recentPrograms, setRecentPrograms] = useState(() => {
        const saved = localStorage.getItem('win95-recent-programs')
        return saved ? JSON.parse(saved) : []
    })

    useEffect(() => {
        localStorage.setItem('win95-recent-programs', JSON.stringify(recentPrograms))
    }, [recentPrograms])

    const addRecentProgram = useCallback((programId) => {
        if (!TRACKABLE_PROGRAMS.includes(programId)) return

        setRecentPrograms((prev) => {
            // Remove if already exists, then add to front
            const filtered = prev.filter((id) => id !== programId)
            return [programId, ...filtered].slice(0, MAX_RECENT)
        })
    }, [])

    const clearRecentPrograms = useCallback(() => {
        setRecentPrograms([])
    }, [])

    return (
        <RecentProgramsContext.Provider
            value={{ recentPrograms, addRecentProgram, clearRecentPrograms }}
        >
            {children}
        </RecentProgramsContext.Provider>
    )
}

export function useRecentPrograms() {
    const context = useContext(RecentProgramsContext)
    if (!context) {
        throw new Error('useRecentPrograms must be used within a RecentProgramsProvider')
    }
    return context
}

import { createContext, useContext, useState, useEffect } from 'react'

const ThemeContext = createContext(null)

const defaultTheme = {
    titlebarStart: '#000080',
    titlebarEnd: '#1084d0',
    taskbarStart: '#d4d0c8',
    taskbarEnd: '#a8a0a0',
    buttonBg: '#c0c0c0',
    activeTaskBtn: '#000000',
    startBtnStart: '#000080',
    startBtnEnd: '#1084d0',
    accentColor: '#000080'
}

export function ThemeProvider({ children }) {
    const [theme, setTheme] = useState(() => {
        const saved = localStorage.getItem('win95-theme')
        return saved ? JSON.parse(saved) : defaultTheme
    })

    const [soundEnabled, setSoundEnabled] = useState(() => {
        const saved = localStorage.getItem('win95-sound')
        return saved ? JSON.parse(saved) : false
    })

    useEffect(() => {
        localStorage.setItem('win95-theme', JSON.stringify(theme))

        // Apply CSS custom properties
        const root = document.documentElement
        root.style.setProperty('--titlebar-start', theme.titlebarStart)
        root.style.setProperty('--titlebar-end', theme.titlebarEnd)
        root.style.setProperty('--taskbar-start', theme.taskbarStart)
        root.style.setProperty('--taskbar-end', theme.taskbarEnd)
        root.style.setProperty('--button-bg', theme.buttonBg)
        root.style.setProperty('--active-task-btn', theme.activeTaskBtn)
        root.style.setProperty('--start-btn-start', theme.startBtnStart)
        root.style.setProperty('--start-btn-end', theme.startBtnEnd)
        root.style.setProperty('--accent-color', theme.accentColor)
    }, [theme])

    useEffect(() => {
        localStorage.setItem('win95-sound', JSON.stringify(soundEnabled))
    }, [soundEnabled])

    const updateTheme = (key, value) => {
        setTheme((prev) => ({ ...prev, [key]: value }))
    }

    const resetTheme = () => {
        setTheme(defaultTheme)
    }

    const toggleSound = () => {
        setSoundEnabled((prev) => !prev)
    }

    return (
        <ThemeContext.Provider
            value={{ theme, updateTheme, resetTheme, defaultTheme, soundEnabled, toggleSound }}
        >
            {children}
        </ThemeContext.Provider>
    )
}

export function useTheme() {
    const context = useContext(ThemeContext)
    if (!context) {
        throw new Error('useTheme must be used within a ThemeProvider')
    }
    return context
}
