                                    borderStyle:
                                        cell.state === CELL_STATE.REVEALED ? 'solid' : 'outset',
                                    borderColor:
                                        cell.state === CELL_STATE.REVEALED ? '#808080' : '#fff', // Outset uses light/dark automatic, but specific colors help
                                    background:
                                        cell.state === CELL_STATE.REVEALED && cell.isExploded
