        [currentDir, openWindow, triggerBSOD, fileSystem]
    )

    // Handle key events
    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            executeCommand(currentInput)
            setCurrentInput('')
        } else if (e.key === 'ArrowUp') {
            e.preventDefault()
            if (commandHistory.length > 0) {
                const newIndex =
                    historyIndex === -1 ? commandHistory.length - 1 : Math.max(0, historyIndex - 1)
                setHistoryIndex(newIndex)
                setCurrentInput(commandHistory[newIndex])
            }
        } else if (e.key === 'ArrowDown') {
            e.preventDefault()
            if (historyIndex !== -1) {
                const newIndex = historyIndex + 1
                if (newIndex >= commandHistory.length) {
                    setHistoryIndex(-1)
                    setCurrentInput('')
                } else {
                    setHistoryIndex(newIndex)
                    setCurrentInput(commandHistory[newIndex])
                }
            }
        }
    }

    // Get color class for output type
    const getColorClass = (type) => {
        switch (type) {
            case 'error':
                return 'terminal-error'
            case 'success':
                return 'terminal-success'
            case 'warning':
                return 'terminal-warning'
            case 'system':
                return 'terminal-system'
            case 'dir':
                return 'terminal-dir'
            case 'command':
                return 'terminal-command'
            default:
                return ''
        }
    }

    return (
        <div className="terminal-container" onClick={handleContainerClick} ref={containerRef}>
            <div className="terminal-output">
                {history.map((line, i) => (
                    <div key={i} className={`terminal-line ${getColorClass(line.type)}`}>
                        {line.text}
                    </div>
                ))}
            </div>
            <div className="terminal-input-line">
                <span className="terminal-prompt">{currentDir}&gt; </span>
                <input
                    ref={inputRef}
                    type="text"
                    className="terminal-input"
                    value={currentInput}
                    onChange={(e) => setCurrentInput(e.target.value)}
                    onKeyDown={handleKeyDown}
                    autoFocus
                    spellCheck={false}
                />
            </div>
        </div>
    )
}

